dh-custom/checkout.scss:
@font-face {
    font-family: 'Gellatio';
    src: url("{{cdn 'assets/fonts/Gellatio-400.ttf'}}") format('truetype');
    font-weight: 400;
    font-style: normal;
    font-display: swap;
}

@import url('https://fonts.googleapis.com/css2?family=Raleway:wght@400;500;600&display=swap');


html {
    -webkit-font-smoothing: antialiased;
    -webkit-text-size-adjust: 100%;
    font-size: 62.5%;
    scroll-behavior: smooth;
}

body {
    padding: 0;
    margin: 0;
    font-size: 1.3rem;
    line-height: 1.5;
    color: #272C49;
    outline: none !important;
    overflow-x: hidden;
    font-family: 'Raleway', Arial, sans-serif;
}


h2,
.h2,
h3,
.h3,
h4,
.h4 {
    margin: 0 0 3rem 0;
    padding: 0;
    font-weight: 700;
    text-decoration: none;
}

.button {
    height: auto;
    @include border-radius(100px);
    outline: none;
    margin: 0;
    padding: 1.4rem 4rem;
    font-size: 1.3rem;
    font-weight: 600;
    line-height: 1.5;
    background-color: #b48b31;
    color: white;
    border: 0.1rem solid #b48b31;
    position: relative;
    text-decoration: none !important;
    overflow: hidden;
    transition: all 0.5s linear;
    -webkit-transition: all 0.5s linear;
    font-family: 'Raleway', Arial, sans-serif;
    text-transform: none;

    &+.button {
        @include mobile {
            margin-top: 1.8rem;
        }
    }

    &:hover {
        background-color: #272c49;
        border-color: #272c49;
        color: white;

        svg {
            fill: #fff;
        }
    }



    &.button--primary {
        background: #ffffff;
        border: 0.1rem solid #272c49;
        color: #272c49;

        &:hover {
            color: #ffffff;
            background: #272c49;
            border: 0.1rem solid #272c49;
        }

        &:active,
        &:focus {
            color: #272c49;
            background: #fff;
            border: 0.1rem solid #272c49;
        }
    }

    &.brown-outline {
        &:hover {
            border-color: #272c49;
            color: #272c49;
            background: #ffffff;
        }
    }
    &--tertiary{
        .icon{
            svg{
                vertical-align: middle;
            }
        }
    }
    &--tiny{
        padding: .8rem 2rem;
    } 
}

.form-input {
    border: 0.1rem solid #272c49;
    background: #ffffff;
    @include border-radius(1.2rem);
    padding: 1.6rem 2rem;
    font-weight: 400;
    font-size: 1.3rem;
    outline: none;
    color: #272C49;
    height: auto;
    -webkit-appearance: none !important;
    -moz-appearance: none !important;
    appearance: none !important;

    &:focus {
        border-color: #D4D5DB;
        box-shadow: none;
    }
}

.form-actions .button,
.form-actions input,
.form-actions a {
    vertical-align: middle;
}

.form-input::-webkit-input-placeholder {
    color: #272C49;
    opacity: 1;
}

.form-input::-moz-placeholder {
    color: #272C49;
    opacity: 1;
}

.form-input:-ms-input-placeholder {
    color: #272C49;
    opacity: 1;
}

.form-input:-moz-placeholder {
    color: #272C49;
    opacity: 1;
}

input:-webkit-autofill,
input:-webkit-autofill:hover,
input:-webkit-autofill:focus,
input:-webkit-autofill:active {
    -webkit-box-shadow: 0 0 0 3rem #272C49 inset !important;
    -webkit-text-fill-color: #272C49;
}

input:autofill,
input:autofill:hover,
input:autofill:focus,
input:autofill:active {
    background: #fff;
    /* or any other */
    box-shadow: none !important;
}

/*======== New-Checkout input css=========*/
.form-field .floating-input,
.form-field .floating-select {
    height: 5rem;
    background-color: transparent;
    font-size: 1.3rem;
    padding-left: 2rem;
    padding-right: 2rem;
}

.form-field .floating-input:not(:focus),
.form-field .floating-select:not(:focus),
.form-field .floating-textarea {
    border-color: #272C49;
}

.form-field {

    .floating-label {
        font-size: 1.3rem;
        line-height: 1.5rem;
        top: 50%;
        left: 2rem;
        transform: translate(0, -50%);

        small {
            position: unset;
        }
    }

    .floating-select {
        padding-left: 2rem;
    }

    .floating-select-chevron {
        display: none;
    }
}

.form-field .react-datepicker__tab-loop+.floating-label,
.form-field .react-datepicker-wrapper+.floating-label,
.form-field select+.floating-label,
.form-field textarea:focus+.floating-label,
.form-field textarea:not(:empty)+.floating-label,
.form-field input:focus+.floating-label,
.form-field input:not(:placeholder-shown)+.floating-label {
    color: #272C49;
    font-size: 1.2rem;
    top: 1.4rem;

    small {
        color: #272C49;
        font-size: 1rem;
        top: 1.4rem;
    }
}

.form-field-group,
.form-row .form-field-group {
    border: 0.1rem solid #D4D5DB;
    @include border-radius(0);
    padding: 2rem;
}

.form-field--error {
    .form-field-errors {
        padding-left: 0.6rem;
    }

    .form-label {
        top: 2.6rem;
    }

    .form-inlineMessage {
        font-size: 1.3rem;
    }

    .form-toggle-password.form-input-icon {
        top: 2.3rem;
    }
}

// .form-checkbox+.form-label,
// .form-radio+.form-label {
//     line-height: 2.4rem;
// }

input:focus+label {
    outline: none !important;
}

.form-checkbox+.form-label,
.form-radio+.form-label {
    outline: none !important;
    outline-width: inherit !important;
    font-weight: 600;
    font-size: 1.3rem;
    line-height: 1.5;
    color: #272c49;
    margin-bottom: 1rem;

    small {
        font-weight: 400;
        font-size: 1rem;
        line-height: 1.1rem;
        text-align: right;
        text-transform: uppercase;
        color: #272C49;
    }
}

p {
    font-size: 1.3rem;
}

/*Checkout custom style*/
.layout {
    width: 114rem;
    padding: 0;
    max-width: 100%;

    @include tablet {
        width: 960px;
    }

    @include tablet-small {
        width: 100%;
        padding-left: 2rem;
        padding-right: 2rem;
        padding-bottom: 7rem;
    }

    .layout-main {
        @include tablet-small {
            padding-left: 0px;
            padding-right: 0px;
        }

    }
}

.checkout-form {
    .form-body {
        p {
            font-size: 1.3rem;

            &:last-child {
                margin-bottom: 0;
            }
        }
    }
}

.customerEmail-container {
    display: block;

    .customerEmail-action {
        margin: 0 0 0;
        padding-left: 2rem;

        @include tablet-small {
            margin: 2rem 0 0 0;
            padding-left: 0;
        }

        .button {
            padding: 1.8rem 1.6rem 1.6rem;
            line-height: 1.3rem;
            height: auto;
        }
    }
}

p {
    a {
        position: relative;
        color: #272c49;
        text-decoration: underline;
        text-underline-offset: 0.3rem;

        &:hover,
        &:focus {
            color: #b48b31;
        }
    }
}

.form-fieldset.redeemable-payments {
    color: #b48b31;
    font-size: 1.3rem;
    position: relative;
    display: none;

    &:hover {
        color: #272c49;
    }
}

.optimizedCheckout-header {
    background-color: #fff;
    border-color: #D4D5DB;

    .checkoutHeader-logo {
        max-width: 13.7rem;

        @include tablet-small {
            max-width: 9rem;
        }

    }
}

.stepHeader-title {
    font-weight: 600;
    min-width: 10.7rem;
    font-size: 1.8rem;
}

.stepHeader-figure {
    height: auto;
}

.stepHeader {
    align-items: flex-start;

    @include tablet-small {
        padding-bottom: 0;
        padding-top: 0;
    }

    .stepHeader-body {
        margin-top: 0rem;
    }
}

.shippingOption {
    span {
        font-size: 1.3rem;
    }
}
.checkout-view-header{
    position: relative;
}
.customerView {
    align-items: flex-start;

    .customerView-body {
        font-size: 1.3rem;
        color: #272C49;
    }
    &-actions{
        @include mobile{
            position: absolute;
            right: 0;
            top: .8rem;
        }
    }
}

.form-legend {
    font-weight: 600;
    font-size: 1.3rem;
    padding-bottom: 0;
    margin-bottom: 1.5rem;
    padding-top: 0;
}

.checkoutHeader-content {
    padding: 2rem;
    max-width: 114rem;
}

.form-fieldset {
    margin-bottom: 3rem;

    @include tablet-small {
        margin-bottom: 2rem;
    }

    .form-body {
        .checkout-address {
            margin: 0 -1rem;
        }

        .dynamic-form-field.floating-form-field {
            margin-bottom: 3rem;
            padding: 0 1rem;

            @include tablet-small {
                margin-bottom: 2rem;
            }
            .form-field{
                margin-bottom: 0;
            }
            &:last-child,
            &:nth-last-child(2) {
                margin-bottom: 1rem;

                @include tablet-small {
                    margin-bottom: 1.5rem;
                }
            }

        }
    }
}

.form-label {
    font-weight: 600;
    font-size: 1.3rem;
    line-height: 1.5;
    color: #272c49;
    margin-bottom: 1.5rem;
    position: relative;

    small {
        font-weight: 400;
        font-size: 1.2rem;
        line-height: 1.5;
        text-align: right;
        text-transform: uppercase;
        color: #272C49;
        vertical-align: middle;
        position: absolute;
        right: 0;
        top: 0.6rem;
    }
}

.form-select {
    border-radius: 0;
