        }
    }
}

.form-body .form-fieldset {
    .dropdown-toggle--select {
        border-color: #272c49;
        color: #272c49;
        font-weight: 600;
        font-family: 'Raleway', Arial, sans-serif;
        line-height: 1.5;
        padding: 1.2rem 2rem;
        @include border-radius(1.2rem);

        &::after {
            border-color: #272c49 transparent transparent transparent;
            right: 2rem;
        }

        &:hover {
