            }

            &::after {
                border-color: #b48b31 transparent transparent transparent;

            }
        }

        &:focus {
            box-shadow: none;
        }
    }
}

.dropdown-toggle--select p {
    color: #272c49;
    font-weight: 400;
}

.form-checkbox+.form-label,
.form-radio+.form-label {
    line-height: 1.5;
    margin-bottom: 0;
