    box-shadow: none;
}



.optimizedCheckout-form-radio:checked+.optimizedCheckout-form-label::before,
.optimizedCheckout-form-checkbox:checked+.optimizedCheckout-form-label::before {
    background-color: transparent;
    border-color: transparent;
    box-shadow: none;
