    }
}

.shippingOptions-container .form-checklist-header--selected .optimizedCheckout-form-checklist-checkbox:checked~.form-label::before {
    top: 1.6rem;
}

.shippingOptions-container .form-checklist-header--selected .optimizedCheckout-form-checklist-checkbox:checked~.form-label::after {
    top: 1.6rem;
}

.form-checklist-header--selected .optimizedCheckout-form-checklist-checkbox:checked~.form-label::before {
