    border: none;
}
.form-checklist-checkbox~.form-label::before,
.form-checklist-checkbox~.form-label::after {
    top: 2.2rem;
    width: 1.6rem;
    height: 1.6rem;
    @include mobile{
        top: 1.7rem;
    }
}
.checkout-step {
    .stepHeader-title.optimizedCheckout-headingPrimary {
        color: #272c49;
    }
}

/*--Place order button--*/
#checkout-payment-continue.button {
    padding: 1.6rem 1.6rem;
}

/*--Place order button--*/


.layout-cart,
.modalOverlay--afterOpen {

    .optimizedCheckout-orderSummary-cartSection,
    .optimizedCheckout-orderSummary {
        border-color: #D4D5DB;
        box-shadow: none;
        border-radius: 0;
    }

