        }
    }

    .cart-section {
        .cart-section-heading {
            font-size: 1.2rem;
            font-weight: 600;
        }

        .button--tiny {
            display: flex;
            margin: 1.8rem auto 0.7rem;

            @include tablet-small {
                margin-top: 2rem;
            }

            .icon {
                margin-left: 0.4rem;

                svg {
                    fill: #ffffff;
                }
            }
        }

        .cart-priceItem--total {
            .cart-priceItem-value {
                .cart-priceItem-value {
                    font-size: 1.8rem;
                }
            }
            .cart-price-label {
                font-size: 1.3rem;
            }

        }

        .cart-priceItem-label,
        .cart-priceItem-value {
            span {
                color: #272c49;
                font-weight: 600;
            }
        }


        .redeemable-label {
            color: #272c49;
            position: relative;
            text-decoration: underline;
            text-underline-offset: 0.3rem;
            font-weight: 600;

            &:hover,
            &:focus {
                color: #b48b31;
            }
        }

        .form-prefixPostfix .button {
            height: auto;
            margin-left: 1rem;
        }

        .product-column {
            .product-title {
                font-size: 1.3rem;
                font-weight: 600;

                @include tablet-small {
                    margin-top: 1.5rem;
                }
            }


            .product-price {
                font-size: 1.3rem;
                color: #b48b31;
                font-weight: 600;
            }

            .productImage-giftCertificate {
                padding: 0;

                svg {
                    width: 100%;
                    display: block;
                    height: 100%;
                }
            }

            .product-option {
                font-size: 1.2rem;
                color: #272C49;
            }
        }
    }

    .cart-priceItem--total {
        .cart-price-value {
            color: #272c49;
        }
    }

    .cart-header {
        display: flex;
        justify-content: space-between;
        @include align-item(center);
    }
}

.layout-cart,
.modalOverlay--afterOpen {
    .optimizedCheckout-orderSummary {
        @include border-radius(1.2rem);
    }
}

.checkout-steps {
    .shippingOption--selected {
        border-color: #D4D5DB;
    }
}

/*Checkout payment style*/
.creditCardTypes-list-item{
    width: 3.4rem;
    .icon--medium {
        height: 3.4rem;
        width: 3.4rem;
    }
    
}
.paymentProviderHeader-container {
    @include align-item(center);
    @media screen and (max-width:412px) {
        display: block;
    }

    .creditCardTypes-list {
        @media screen and (max-width:412px) {
            margin-left: 0;
            justify-content: flex-start;
        }
    }
}

.checkout-step--payment {
    .form-label {

        a {
            color: #b48b31;
            position: relative;
            text-decoration: underline;
            text-underline-offset: 0.4rem;

            &:hover {
                color: #272c49;
            }
        }
    }
}

.form-field--ccNumber .form-input {
    height: 4.5rem;
}

.form-field--ccExpiry .form-input {
    height: 4.5rem;
    padding: 1.3rem 1.6rem;
}

.form-field--ccName .form-input {
    height: 4.5rem;
    padding: 1.3rem 1.4rem;
}

.form-ccFields-field--ccCvv {
    .form-label {
        display: flex;
        align-items: center;
    }

    .form-input {
        height: 4.5rem;
        padding: 1.3rem 1.8rem;
    }
}

.optimizedCheckout-form-checklist-item:hover,.optimizedCheckout-form-checklist,
.optimizedCheckout-form-checklist-item--selected,
.optimizedCheckout-form-checklist {
    background-color: #FAF9F9;
    @include border-radius(1.2rem);
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
}

.form-checklist-item {
    &:last-child {
        border-bottom-left-radius: 1.2rem;
        border-bottom-right-radius: 1.2rem;
    }

    .paymentMethod--creditCard {
        margin-bottom: 1.5rem;
    }
}

.form-fieldset.redeemable-payments {
    .redeemable-entry .form-input {
        width: 100%;
        max-width: 100%;
        margin-right: 2rem;
    }
}

.form-checklist:only-child,
.checklist-skeleton:only-child {
    @include border-radius(1.2rem);
    overflow: hidden;
}

.form-checklist-body {
    .optimizedCheckout-form-checklist-item--selected {
        border-color: #D4D5DB;
        border-radius: 0px;
    }
    .form-field--ccNumber, .form-field--ccName {
        @include mobile{
            flex-basis: 100%;
        }
    }
    .form-field{
        @include mobile{
            margin-bottom: 2rem !important;
        }
    }

    .form-fieldset {
        .form-input {
            @include border-radius(1.2rem);
        }

    }

    /*cvv and icon changes */
    .form-input.has-icon+.icon,
    .has-icon.widget--stripev3+.icon {
        height: 4.4rem;
        margin-left: -2.8rem;
        width: 2rem;
    }

    /*cvv and icon changes */
}

/* pop up css*/
.redeemable-payments {
    .form-field {
        .button {
            height: auto;
        }
    }
}

.modalOverlay--afterOpen {
    .cart-section-heading {
        font-size: 1.3rem;
    }

    .optimizedCheckout-orderSummary.modal-header {
        padding: 1.6875rem 1.875rem;
        border-bottom: 0;
    }

    .optimizedCheckout-orderSummary {
        box-shadow: none;
        border-bottom: 0.1rem solid #D4D5DB;
        padding-left: 0;
        border-radius: 0;
        padding-right: 0;
    }

    .optimizedCheckout-orderSummary-cartSection {
        padding: 1.6875rem 1.875rem;
    }

    .cart-modal-close {
        .icon {
            width: 1.8rem;
            height: 1.8rem;

            svg {
                fill: #b48b31;
            }
        }
    }

    .modal-header-title {
        font-size: 2.2rem;

        @include tablet-small {
            font-size: 1.5rem;
        }

        .cart-heading-subheader {
            font-size: 1.3rem;
            font-weight: 600;

        }
    }

    .optimizedCheckout-contentPrimary.modal--afterOpen {
        border: 1px solid #D4D5DB;
        box-shadow: none;
        @include border-radius(1.2rem);
    }

    .cart-modal-link {
        position: relative;
        color: #b48b31;
        text-decoration: underline;
        font-weight: 600;

        &:hover {
            color: #272c49;
        }
    }

}

.cartDrawer {
    border-radius: 0;
    z-index: 125;
    padding: 1rem 2rem;
    @include border-radius(1.2rem);

    .cartDrawer-body {
        a {
            position: relative;
            color: #b48b31;
            text-decoration: underline;

            &:hover {
                color: #272c49;
            }
        }
    }

    &-figure {
        border: 0;

        img {
            @include border-radius(1.2rem);
            padding: 0.6rem;
        }
    }
}

