/* ------- Pagination Style ------- */
.pagination {
  max-width: 67.8rem;
  margin: 0 auto;

  &-list {
    position: relative;

    @include tablet-small {
      margin-bottom: 0;
    }

    li {
      border: 0;

      &.pagination-item--previous {
        left: 0;
      }

      &.pagination-item--next {
        right: 0;
      }

      &.pagination-item--next,
      &.pagination-item--previous {
        margin-right: 0;
        margin-left: 0;
        position: absolute;
        top: 50%;
        transform: translate(0, -50%);

        a {
          border: 0;
          @include display-flex(flex);
          @include align-item(center);
          @include justify-content(center);
          font-size: 0;
          padding: 0;
          line-height: 1.4rem;
          @include border-radius(1.6rem);

          &:hover {
            background-color: $color-white;

            svg {
              fill: $color-secondary;
            }
          }
        }

        .icon {
          width: 1.6rem;
          height: 1.6rem;
          margin: 0;
        }

        svg {
          fill: $color-textBase;
          // width: 1.4rem;
          // height: 1.4rem;
        }
      }

      &.pagination-item--current {
        margin: 0 0 0 1.5rem;

        a {
          border: 0;
          background-color: $color-secondary;
          color: $color-white;
          padding: 1.2rem 1.8rem;
        }
      }

      .pagination-link,
      a {
        padding: 1.1rem 1.75rem;
        @extend .fs-14;
        line-height: 2rem;
        font-weight: 600;
        border: 1px solid #E9EAED;
        color: $color-primary;
        &:hover {
          background-color: $color-secondary;
          border-color: $color-secondary;
          color: $color-white;
        }
      }

      .mobile-page,
      span {
        color: $color-primary;
        @extend .fs-16;
        line-height: 1.5;
        font-weight: 600;
      }
      .devider{
        margin: 0 2rem;
        vertical-align: middle;
        margin-top: .1rem;
      }

      +li {
        margin-left: 1.5rem;
        margin-right: 0;
      }

    }
  }
}

/* ------- Pagination Style ------- */
/* ========== Top banner + Page Space ========== */
.bg-cream-banner+.common-t-s-page {
  margin-top: 5rem;

  @include mobile {
    margin-top: 3rem;
  }
}

