}

.accordion {
    &-block {
        border: 1px solid $color-greyLight;
        @include border-radius(1.2rem);

        &:first-child {

            button,
            .accordion-navigation {
                @include border-radius(1.2rem);
            }
        }

        &:last-child {
            button.accordion-navigation {
                @include border-radius(1.2rem);
                border-bottom-width:1px;
            }

            button.is-open.accordion-navigation {
                @include border-radius(1.2rem);
                border-bottom-left-radius: 0;
                border-bottom-right-radius: 0;
            }
        }


    }

    &--navList {
        .accordion-block {
            margin-top: 1rem;

            &:first-child {
                @include tablet-small {
                    margin-top: 0;
                }
            }
        }

        .accordion-navigation {
            padding: 1.2rem 2rem;
            @include border-radius(1.2rem);
            background-color: $color-textBase;
            transition: all .5s ease;
            border: 1px solid transparent;

            .accordion-title {
                color: $color-white;

            }
            span{
                &:not(.accordion-title){
                    line-height: 0;
                }
            }

            &:hover {
                background-color: $color-textSecondary;
                border-color: $color-textSecondary;

                @include tablet-small {
                    background-color: $color-primary;
                    border-color: $color-primary;
                }
            }

            svg {
                fill: $color-white;
            }

            &.is-open {
                border: 0.1rem solid $color-greyLight;
                border-bottom: .5px solid rgba($color-primary, 0.20);
                background-color: $color-white;
                @include border-radius(1.2rem);
                border-bottom-left-radius: 0;
                border-bottom-right-radius: 0;
                svg {
                    fill: $color-textBase;
                }

                &:hover {
                    border-color: $color-textSecondary;
                    @include tablet-small{
                        border-color: rgba($color-primary, 0.20);
                    }
                    .accordion-title {
                        color: $color-white;

                        @include tablet-small {
                            color: $color-primary;
                        }
                    }

                    svg {
                        fill: $color-white;

                        @include tablet-small {
                            fill: $color-primary;
                        }
                    }
                }

                .accordion-title {
                    color: $color-textBase;

                    .accordion-title {
                        color: $color-white;
                    }
                }

                &:hover {
                    background-color: $color-textSecondary;

                    @include tablet-small {
                        background-color: $color-white;
                    }
                }

            }
        }
    }
}

.facetedSearch {
    padding: 0;

    &-toggle+&-navList {
        margin-top: 0rem;
    }

    &-toggle+&-navList.is-open {
        margin-top: 0rem;

        .accordion-block {
            &:first-child {
                .accordion-navigation {
                    @include border-radius(1.2rem);
                    border-bottom-left-radius: 0;
                    border-bottom-right-radius: 0;
                }

            }
        }
    }

    .inlineList--labels {
        margin: 0;

        li {
            margin: 0;
            margin-bottom: 1rem;
            margin-right: 1rem;

