/*----- HeroCarousel -----*/
.videoCarousel {
    margin-top: 0;
    margin-bottom: 0;
    position: relative;
    z-index: 0;
    &-inner {
        position: relative;
    }

    .heroCarousel-image-wrapper {
        position: relative;
        // New for object fit cover. Daniel V.
        top: 0;
        left: 0;
        width: 100%;
        overflow: hidden;
        // End new
        //height: 70rem;
        height: calc(100svh - 10.8rem);

        @include tablet-small {
            //height: 50rem;
            height: calc(100svh - 9.6rem);
        }

        @include mobile {
            //height: 41.5rem;
        }

        video {
            width: 100%;
            object-fit: cover;
            display: block;
        }

        iframe {
            width: 100%;
            height: 100%;
            width: 100vw;
            // New for object fit cover. Daniel V.
            height: 56.25vw;
            min-height: 100vh;
            min-width: 177.77vh;
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            // End new
        }

        &::after {
            content: "";
            position: absolute;
            left: 0;
            right: 0;
            top: 0;
            bottom: 0;
            background: rgba($color-primary, .6);
            z-index: 1;
            pointer-events: none;
        }
    }

    .heroCarousel-content {
        position: absolute;
        top: 50%;
        @include transform(translateY(-50%));
        padding: 0;
        background-color: transparent;
        z-index: 2;

        @include tablet-small {
            left: 0;
            right: 0;
            margin: 0 auto;
        }

        .heroCarousel-title {
            @extend .fs-48;
            color: $color-white;
            margin-bottom: 2rem;

            @include tablet {
                font-size: 3.5rem
            }

            @include mobile {
                font-size: 2.4rem
            }
            sup{
                font-size: 0.2916666666666667em;
                margin-left: .8rem;
                top: -2rem;
            }

            span {
                @extend .font-secondory;
                font-size: 0.875em;
                line-height: normal;
                font-weight: 400;
                vertical-align: middle;

                @include tablet {
                    font-size: 2rem
                }
            }
        }
    }
}

/*----- Shop By Category Section -----*/
.shopByCategory {
    @extend .bg-secondary-light;
    @extend .pb-40;
    @include wave(secondaryLight);

    .page-heading {
        padding-left: 3.2rem;
        margin-bottom: 0;
        @extend .pt-40;
        @include tablet {
            margin-bottom: 2rem;
        }

        @include tablet-small {
            margin-bottom: 4.1rem;
            padding-left: 0;
            text-align: center;
        }
    }

    .container {
        width: 122rem;
    }

    &-list {
        @include display-flex(flex);
        @include flex-wrap(wrap);
        //margin-top: 5.8rem;
        margin-top: 3rem;
        //@include row;
        @include tablet-up{
            padding-bottom: 6rem;
        }
        @include tablet {
            margin-top: 2rem;
        }

        @include tablet-small {
            margin-top: 4.1rem;
        }
        &.slick-dotted{
            @include tablet-small{
                margin-bottom: 0;
            }
            .slick-dots{
                bottom: 0;
                li
                {
                    button{
                        @include tablet-small{
                            background-color: rgba($color-secondary,0.3);
                        }
                    }
                    &.slick-active{
                        button{
                            background-color: $color-secondary;
                        }
                        
                    }
                } 
            }
        }
    }

    &-item {
        width: calc(100% /3);
        //@include col;
        margin-bottom: 2.4rem;
        @include tablet-small{
            margin-bottom: 4.6rem;
        }

        .img-block {
            position: relative;
            @extend .bg-secondary-light;

            img {
                width: 35.1rem;
                max-height: 23.2rem;
                object-fit: contain;
                mix-blend-mode: darken;
                display: block;
                margin: auto;

                @include tablet-small {
                    opacity: 0;
                    width: 33.1rem;
                }

                @include mobile {
                    width: 29.4rem;
                }

                +img {
                    position: absolute;
                    left: 0;
                    right: 0;
                    top: 0;
                    opacity: 0;
                    margin: auto;
                    transition: opacity 0.5s linear;

                    @include tablet-small {
                        opacity: 1;
                    }
                }
            }


        }

        .content {
            @extend .text-center;
            margin-top: 2.2rem;
            padding-left: 0;
            padding-right: 0;
            max-width: 31.5rem;
            margin-left: auto;
            margin-right: auto;

            h2 {
                @extend .fs-18;
                font-family: 600;
                line-height: normal;
                margin-bottom: 1rem;

                @include tablet-small {
                    font-size: 2.2rem;
                    margin-bottom: 1.6rem;
                    color: $color-secondary;
                }
            }

            p {
                @extend .fs-14;
                margin-bottom: 0;
                clip-path: inset(0% 0 100% 0);
                opacity: 0;
                //@include transform(translateY(-15px));
                transition: all 0.2s linear;

                @include tablet-small {
                    opacity: 1;
                    clip-path: inherit;
                }
            }
        }

        &:hover {
            .img-block {
                img {

                    //opacity: 0;
                    +img {
                        opacity: 1;
                    }
                }
            }

            .content {

                h2 {
                    color: $color-secondary;
                }

                p {
                    opacity: 1;
                    //@include transform(translateY(0%));
                    clip-path: inset(0% 0 0 0);
                }
            }
        }

    }
}

