            font-size: 2.8rem;
            font-weight: 700;
            text-align: center;
            margin-bottom: 2.8rem;
            @include tablet{
                font-size: 2rem
            }
            @include mobile{
                font-size: 1.6rem;
            }
        }
    }
}

/*----- Product Features -----*/
.product-features {
    @extend .bg-light-green;
    @extend .pt-40;
    @extend .pb-40;
    @include wave-no(greenLight);

    @include tablet-small {
        // padding: 5.5rem 0 6rem;
    }
    .wave-no{
        top: -9.5rem;
        transform: translateY(-100%);
        position: absolute;
        top: 1px;
        left: 0;
        right: 0;
        z-index: 1;
    }
    h2.page-heading {
        margin-bottom: -.2rem;

        @include tablet-small {
            text-align: center;
            margin-bottom: .3rem;
        }
    }

    .object-circle {
        right: 8.5rem;
        bottom: -6rem;

        @include mobile {
            width: 2rem;
            height: 2rem;
            right: 5.4rem;
            bottom: -5rem;
        }
    }

    .object-halfRoundedBorders {
        right: 31rem;
        top: -6rem;
        transform: translateY(calc(-100% + 4.4rem));
        display: none;

        @include desktop-small {
            right: 10rem;
        }

        @include tablet-small {
            right: 7rem;
            top: -9.3rem;
        }

        @include mobile {
            right: 4rem;
        }
    }

    &-image {
        display: none;

        &.slick-initialized.slick-slider {
            display: block;
            margin-bottom: 1.5rem;
        }

        .img-block {
            @extend .bg-light-green;
        }

        img {
            max-height: 60rem;
            object-fit: contain;

            @include tablet-small {
                max-width: 30rem;
                max-height: 30rem;
                margin: 0 auto;
            }
        }
    }

    &-content {
        position: static;
        padding-left: 5rem;
        display: none;

        &.slick-initialized.slick-slider {
            display: block;
            @include tablet-small{
                margin-bottom: 0rem;
            }

        }

        @include tablet-small {
            padding-left: 1rem;
            padding-right: 4rem;
        }

        @include mobile {
            padding-left: 0;
            padding-right: 0;
        }

        .slick-dots {
            position: relative;
            text-align: left;
            top: 10rem;
            bottom: auto;
            button {
                background: rgba($color-primary, 0.3);
            }
          
              &.slick-active {
                button {
                  background: $color-secondary;
                }
              }
            @include tablet-small{
                top: 6rem;
            }

            @include mobile {
                top: auto;
                bottom: 0;
                position: relative;
                text-align: center;
                margin-top: 4.3rem;
            }
        }

        .slick-arrow {
            padding: 0;
            width: auto;
            line-height: normal;
            height: auto;

            &.slick-next {
                right: -9rem;

                @include desktop-small {
                    right: -2rem;
                }

                @include tablet {
                    right: 0;
                }
            }

            &.slick-prev {
                left: -9rem;

                @include desktop-small {
                    left: -2rem;
                }

                @include tablet {
                    left: 0;
                }
            }
        }

        .content {
            @include mobile {}
        }

        .page-heading {
            margin-bottom: -.4rem;

            // transform: translate3d(0, 100px, 0);
            // opacity: 0;
            // transition: all 0.2s linear;
            @include mobile {
                margin-bottom: .6rem;
                text-align: center;
            }

            +p {
                margin-bottom: 0;
                max-width: 35.3rem;
                // transform: translate3d(0, 100px, 0);
                // opacity: 0;
                // transition: all 0.2s linear;
                @extend .fs-18;

                @include tablet-small {
                    font-size: 1.4rem;
                }

                @include mobile {
                    text-align: center;
                    max-width: 28rem;
                    margin: 0 auto;
                }
            }
        }



        .slick-active {

            .page-heading,
            p {
                // transform: translate3d(0, 0px, 0);
                // opacity: 1;
                // transition: all 0.8s linear;
                animation-fill-mode: both;
                -webkit-animation-name: fadeInUp;
                animation-name: fadeInUp;
                -webkit-animation-fill-mode: both;
                animation-fill-mode: both;
                -webkit-animation-duration: 0.5s;
                animation-duration: 0.5s;
                animation-delay: 0.4s;
            }

            p {
                //transition-delay: .3s
                animation-delay: 0.6s;
            }
        }
    }
}

/*----- Trending Products -----*/
.trending-products,
.productView-related {
    @extend .pt-40;
    @extend .pb-40;
    @include wave(white);

    .page-heading {
        margin-bottom: 2.5rem;

        @include tablet-small {
            margin-bottom: 1.6rem;
        }
    }

    .container {
        @include tablet-small {
            padding: 0;
        }
    }

    .productCarousel {
        margin-left: 0;
        margin-right: 0;
        display: none;

        @include tablet-small {
            overflow: hidden;
        }

        &.slick-initialized.slick-slider {
            @include display-flex(flex);
        }

        @include display-flex(flex);

        .slick-list {
            @include row;
        }

        .slick-track {
            @include display-flex(flex);
            height: 100%;
        }

        &-slide {
            &.slick-slide {
                height: 100%;
                @include col;
            }

            .card-title {
                overflow-wrap: inherit;
            }
        }

        .slick-arrow {
            padding: 0;
            width: auto;
            line-height: normal;
            height: auto;

            &.slick-next {
                right: -9rem;

                @include desktop-small {
                    right: -2rem;
                }

                @include tablet {
                    right: 0;
                }
            }

            &.slick-prev {
                left: -9rem;

                @include desktop-small {
                    left: -2rem;
                }

                @include tablet {
                    left: 0;
                }
            }
        }
    }

    .card {
        border: 0;
        padding: 0;

        &-figure {
            margin-bottom: 1rem;
        }

        &-image {
            max-width: 18rem;
            min-height: 18rem;
        }

        &-button-block {
            display: none;
        }

        &-body {
            padding-top: 0;
        }
    }
}

/*----- Product Offer Section -----*/
.product-offer {
    @extend .pt-40;
    @extend .pb-40;
    padding-right: 6rem;
    padding-left: 6rem;
    @include tablet-small{
        padding-left: 2rem;
        padding-right: 2rem;
        padding-top: 0;
    }
    &-list {
        margin-right: -3rem;
        margin-left: -3rem;

        @include tablet {
            margin-right: -2rem;
            margin-left: -2rem;
        }
        @include tablet-small{
            margin-left: -1rem;
            margin-right: -1rem;
        }

        @include mobile {
            margin-right: 0rem;
            margin-left: 0rem;
            @include flex-wrap(wrap);
        }
    }

    &-item {
        padding-right: 3rem;
        padding-left: 3rem;
        position: relative;
        @include tablet{
            padding-left: 2rem;
            padding-right: 2rem;
        }
        @include tablet-small{
            padding-left: 1rem;
            padding-right: 1rem;
        }

        @include mobile {
            width: 100%;
            padding-right: 0;
            padding-left: 0;
        }

        +.product-offer-item {
            @include mobile {
                margin-top: 6rem;
            }
        }

        .img-block {
            @extend .mb-40;
            position: relative;

            @include tablet-small {
                margin-bottom: 3rem;
            }

            img {
                border-radius: 40rem;
                max-height: 32rem;
                object-fit: cover;

                @include desktop-small {
                    max-height: inherit;
                    width: 100%;
                }
            }

            .object-dots {
                bottom: -1rem;
                left: 3rem;

                @include tablet-small {
                    right: 3.8rem;
                }
            }

            .object-halfRoundedBordersRight {
                right: 3rem;
                top: -3rem;

                @include tablet-small {
                    right: 1.8rem;
                    width: 7.3rem;
                    height: 7.3rem;
                    top: -1.8rem;
                }

            }

            .object-circle {
                top: 0;
                right: 2rem;
                width: 6rem;
                height: 6rem;

                @include tablet {
                    width: 4.5rem;
                    height: 4.5rem;
                }

                @include tablet-small {
                    width: 3.5rem;
                    height: 3.5rem;
                    right: 1.8rem;
                }
            }

            .object-triAngles {
                bottom: 0;
                left: 3rem;

                @include tablet-small {
                    left: 1.8rem;
                    bottom: -0.6rem;

                }
            }

            .object-cancel {
                bottom: 0;
                transform: translateY(50%);
                right: 20rem;

                @include tablet-small {
                    right: 12.55rem;
                }
            }
        }

        .content {
            text-align: center;
            max-width: 49rem;
            margin: 0 auto;

            @include tablet-small {
                max-width: inherit;
            }

            h2 {
                @extend .fs-24;
                line-height: normal;
                margin-bottom: 1.6rem;
                font-weight: 600;

                @include tablet {
                    font-size: 1.8rem
                }
            }

            p {
                @extend .fs-18;
                margin-bottom: 3rem;
                font-weight: 400;

                @include tablet {
                    font-size: 1.4rem;
                    padding: 0 2rem;
                }

                @include tablet-small {
                    padding: 0 3.8rem;
                }

                @include mobile {
                    padding: 0 4rem;
                }
            }
        }
    }
}

/*----- blog Section - Home -----*/
