/* ========== BLOG GRID STYLE ========== */

/* ========== BLOG DETAIL STYLE ========== */
.page_type__blog {
    .page {
        margin: 0;

        .pagination-list {
            margin-bottom: 0;
            margin-top: 3rem;
        }
    }
}

.template-blog_post {
    .blog-thumbnail {
        width: 100%;
        max-width: 100%;

        a {
            width: 100%;
        }

        img {
            //height: 50rem;
            object-fit: cover;
            aspect-ratio: 312/241;

            @include tablet-small {
                //height: 40rem;
            }

            @include mobile {
                //height: 30rem;
            }
        }
    }

    .blog-post {
        p {
            margin-bottom: 1.7rem;
        }
    }

    h1 {
        font-weight: 600;
    }

    .blog-post-body {
        width: 100%;

        h2,
        h3,
        h4,
        h5 {
            font-weight: 400;

            +p {
                margin-top: 0.8rem;
            }
        }

        .blog-title {
            margin: 0 0 1.5rem;
        }
    }

    .blog-date {
        margin-bottom: 3rem;

    }
    .blog-date, .blog-author{
        color: $color-primary;
    }

    .blog-thumbnail a {
        width: 100%;
    }

    .body {
        .socialLinks {
            @include display-flex(flex);
            @include justify-content(center);

            &::after,
            &::before {
                display: none;
            }

            column-gap: 1rem;

            &-item {
                margin: 0;

                .icon {
                    width: 4.2rem;
                    height: 4.2rem;
                    border: 1px solid rgba($color-primary, 0.1);
                    @include border-radius(50%);

                    svg {
                        fill: $color-primary;
                    }

                    &:hover {
                        border-color: $color-secondary;
                        @extend .bg-secondary;

                        svg {
                            fill: $color-white;
                        }
                    }
                }

            }

            &-item--email,
            &-item--print {
                svg {
                    width: 1.4rem;
                }
            }
        }

    }
}

.blog {
    .tags {
        margin: 0;
        @include tablet-small{
            margin-bottom: 3rem;
        }
        .tag {
            margin: 0 1rem;
            &:first-child{
                @include tablet-up{
                    margin-left: 0;
                }
            }
            @include tablet-small {
                margin: 0 0.7rem;
            }

            @include mobile {
                margin: 0 0.6rem;
                margin-top: 1rem;

                &:nth-child(1) {
                    margin-top: 0;
                }

                &:nth-child(2) {
                    margin-top: 0;
                }

                &:nth-child(3) {
                    margin-top: 0;
                }
            }

            @media screen and (max-width: 400px) {

                &:nth-child(4) {
                    margin-top: 1.1rem;
                }

            }

            .button {
                height: auto;
                @include border-radius(100px);
                outline: none;
                margin: 0;
                padding: 1.6rem 4rem;
                font-size: 1.4rem;
                font-weight: 600;
                color: $color-white;
                line-height: normal;

                @include tablet-small {
                    font-size: 1.1rem;
                }
            }
        }
    }
}
.blog-detail{
    .blog{
        &-title{
            @extend .page-heading;
        }
    }
    .blog-tag-share{
       @include tablet-up{
        @include display-flex(flex);
        @include justify-content(space-between);
        @include align-item(center);
       }
    }
}
