dh-custom/search.scss:
.template-search {
    .panel--large {
        .panel-body {
            margin-bottom: 3rem;
        }
    }
}

.u-hidden+.page-content {
    width: 100%;
}

.search-suggestion {
    margin-bottom: 2rem;
    @extend .fs-14;

    @include tablet-small {
        margin-bottom: 1.5rem;
        font-size: 1.3rem;
    }
    a{
        text-decoration: underline;
    }
}

/*========Search preview=========*/

.quickSearchResults {
    margin-top: 0;

    .productGrid {
        @include tablet-small {
            padding-top: 2rem;
        }

        li {
            @include tablet-small {
                width: 100%;
                padding: 0;
                margin-bottom: 2rem;
            }
        }

        .card {
            @include tablet-small {
                flex-direction: inherit;
                height: auto;
                margin-bottom: 0;
                padding: 1rem;
            }

            &-figure {
                @include tablet-small {
                    width: 6rem;
                }
            }

            &-body {
                @include tablet-small {
                    width: calc(100% - 6rem);
                    display: block;
                    text-align: left;
                }

                .card-button-block {
                    @include tablet-small {
                        display: none;
                    }
                }
            }

            &-img-container {
                img {
                    @include tablet-small {
                        padding: 0.3rem;
                        @include border-radius(1.2rem);
                    }
                }

                &::after {
                    @include tablet-small {
