}

dh-custom/cart.scss:
.cart-header-item {
    padding-bottom: 2rem;
}

.template-cart {
    .body {
        min-height: 45rem;
    }

    .page-content {
        @include mobile {
            padding: 0;
        }

        .select2.select2-container,
        .select.form-select {
            width: 100% !important;
        }

        .select2-container--default {
            .select2-selection--single {
                .select2-selection__arrow {
                    right: 0.5rem;
                }
            }

        }
    }
}

.cart-item {
    border-color: $color-greyLight;
    @include tablet {
        padding-bottom: 2rem;
        position: relative;
    }

    &-figure {
        padding: 3rem 0;
        @include mobile{
            padding: 0;
        }
        img{
            width: 100%;
        }
    }

    &-title {
        padding: 2rem;

        @include tablet {
            padding-top: 0.5rem;
            margin-bottom: 0;
        }

        @include tablet-small {
            padding-top: 0.5rem;
            padding-bottom: 1rem;
        }
    }

    &-name {
        margin-bottom: 1rem;

        @include tablet-small {
            margin-bottom: 0.5rem;
        }

        +.definitionList {
            margin: 0 0 0.8rem;

            @include tablet-small {
                margin-bottom: 0;
            }

            .definitionList-key,
            .definitionList-value {
                font-weight: 400;
                font-size: 1.3rem;
            }

            .definitionList-key {
                color: $color-primary;
                font-weight: 600;
            }

            +a {
                display: block;
                font-weight: 400;
                @extend .fs-13;
                color: $color-primary;
                text-decoration: underline;
                text-underline-offset: 0.4rem;

                &:hover {
                    color: $color-secondary;
                }
            }
        }
    }

    &-brand {
        font-weight: 300;
        margin-bottom: 0.8rem;
    }

    &-name__label {
        @extend .fs-16;
        line-height: 1.5;
        font-weight: 600;
        color: $card-title-color;
        text-transform: none;

        &:hover {
            color: $color-secondary;
        }

        &:active,
        &:focus {
            color: $color-primary;
        }
    }

    &-value {
        font-weight: 600;
        color: $color-primary;
    }

    &-info {
        @include tablet-small {
            padding: 0 0.7rem;
        }

        @include mobile {
            margin-top: 2rem;
        }

        &:last-child {
            @include mobile {
                margin-top: 0;
            }
        }

        .cart-item-value {
            @extend .fs-13;
        }

        span {
            @extend .fs-13;

            &.cart-item-value {
                color: $color-secondary;
            }
        }
    }
    &-price{
        .price{
            color: $color-secondary;
        }
    }
    &-quantity {
        @include tablet-small {
            @include display-flex(flex);
            @include flex-direction(column);
        }

        @include mobile {
            margin: 0.6rem 0px;
            align-items: center;
            flex-direction: inherit;
        }

        .form-increment {
            display: inline-flex;
            border: 0.1rem solid $color-primary;
            @include align-item(center);
            @include justify-content(center);
            @include border-radius(1.2rem);

            @include tablet {
                max-width: 12rem;
                width: 100%;
            }

            .form-input {
                @extend .fs-13;
                color: $color-white;
                font-weight: 600;
            }

            .button {
                border: 0;
                width: 4rem;
                height: 4rem;
                color: $color-primary;
                @include border-radius(1.2rem);
                @include display-flex(flex);
                @include align-item(center);
                @include justify-content(center);

                @include mobile {
                    width: 3.5rem;
                    height: 3.5rem;
                }

                &:hover {
                    .icon {
                        svg {
                            fill: $color-secondary;
                        }
                    }
                }

                .icon {
                    height: 1.1rem;
                    width: 1.1rem;

                    svg {
                        fill: $color-primary;
                    }
                }
            }

            .form-input--incrementTotal {
                border: 0;
                border-left: 0.1rem solid $color-primary;
                border-right: 0.1rem solid $color-primary;
                border-radius: 0;
                color: $color-primary;
                width: 4rem;
                height: 4rem;
                padding: 0;

                @include mobile {
                    width: 3.5rem;
                    height: 3.5rem;
                }

                &:disabled {
                    @include border-radius(1.2rem);
                }
            }
        }
        &.gift-certificate-qty{
            .form-input{
                border: none;
            }
        }
        .form-label{
            @include tablet-small{
                margin-bottom: .4rem;
            }
        }
    }
}

.cart-remove {
    width: 2rem;
    height: 2rem;
    margin-top: 0.3rem;
    background-color: transparent;

    @include tablet {
        position: absolute;
        right: 0;
        top: 1.5rem;
    }

    @include mobile {
        height: 1.8rem;
        width: 1.8rem;
        padding: 0;
        margin: 0;
        top: 1.8rem;
    }

    >svg {
        padding: 0.1rem;
        width: 100%;
        height: 100%;
        fill: $color-primary;
    }

    &:hover {
        svg {
            fill: $color-secondary;
        }
    }
}

.cart {
    margin-bottom: 3rem;

    @include tablet-small {
        margin-bottom: 2.5rem;
    }
    &-list{
        @include tablet-small{
            border-top: none;
        }
    }
}

.cart-total {
    margin-bottom: 0;

    .form-select {
        @extend .form-select;
    }

    .form-input {
        @extend .form-input;

    }

    &-label {
        color: $color-primary;
        padding: 1.28rem 0;
        @extend .fs-13;
    }

    &-value {
        padding: 1.25rem 0;
        @extend .fs-13;

        .shipping-estimate-value {
            color: $color-secondary;
            text-decoration: underline;

            &:hover {
                color: $color-primary;
            }
        }
    }

    &-grandTotal {
        font-size: 1.8rem;
        line-height: 1.5;
        font-weight: 600;
    }
}

.coupon-code-add,
.coupon-code-cancel,
.shipping-estimate-show,
.gift-certificate-add,
.gift-certificate-cancel {
    color: $color-secondary;
    text-decoration: underline;

    &:hover {
        color: $color-primary;
    }
}

.coupon-code-cancel,
.shipping-estimate-show[aria-expanded="true"] {
    font-style: normal;
}

.coupon-form,
.cart-gift-certificate-form {
    margin-bottom: 2rem;
}

.shipping-quotes {
    form {
        margin-bottom: 2rem;
    }

    ul {
        margin-bottom: 1.4rem;
        margin-left: 0 !important;

        li {
            &::before {
                display: none;
            }

            &:last-child {
                margin-bottom: 0;
            }
        }

        dl {
            margin-bottom: 0;
            .estimator-form-input--price{
                margin-top: .6rem;
            }
        }
    }
}

.estimator-form {
    dl {
        margin-bottom: 2rem;
        .estimator-form-label{
            @include mobile{
                width: 100%;
                text-align: left;
            }
        }
        .estimator-form-input{
            @include mobile{
                width: 100%;
            }
        }
    }

    &-input {
        margin-bottom: 1.6rem;
        text-align: left;
        @extend .fs-13;
    }

    &-input--price {
        margin-bottom: 0;
        @extend .fs-13;
    }

    &-label-text {
        position: relative;
        padding-left: 2rem;
        margin-left: 0;
        @extend .fs-13;
    }
    .select2.select2-container,.select.form-select{
        .select2-selection--single{
            @include tablet-small{
                padding: 1.45rem 0;
            }
        }
    }
    &-label {
        padding: 0.6rem 0;
        
        input[type=radio] {
            opacity: 0;
            position: absolute;

            +label {
                @extend .fs-13;
                padding-left: 2.5rem;

                &::before {
                    content: "";
                    position: absolute;
                    border: 0.1 solid $color-greenLight;
                    left: 0;
                    top: 50%;
                    transform: translateY(-50%);
                    width: 1.7rem;
                    height: 1.7rem;
                    @include border-radius(100%);
                    background: rgba($color-secondary, 0.20);
                    border: 0;
                }
            }

            &:checked {
                +label {
                    &::before {
                        border-color: $input-font-color;
                    }

                    &::after {
                        content: "";
                        position: absolute;
                        top: 50%;
                        transform: translateY(-50%);
                        width: 0.9rem;
                        height: 0.9rem;
                        background-color: $color-secondary;
                        background-image: none;
                        margin-top: 0;
                        left: 0.4rem;
                        @include border-radius(100%);
                    }
                }
            }
        }
    }

    .form-inlineMessage {
        @extend .fs-13;
        line-height: 1.5;
    }

    &--default {
        .estimator-form-input {
            text-align: right;

        }

        .estimator-form-row {
            padding-left: 0 !important;
        }
    }
}


.modal-body {
    @include mobile{
        overflow-x: hidden;
    }
    #CartEditProductFieldsForm {
        .form-field {
            margin-bottom: 1rem;

            @include tablet {
                margin-bottom: 1rem;
            }
            &--frameFinish{
                .form-option-swatch{
                    border-radius: 1rem;
                    .form-option-variant{
                        &--pattern{
                            display: none;
                            + .form-option-expanded{
                                position: static;
                                opacity: 1;
                                visibility: visible;
                                transform: none;
                                border: none;
                                text-align: center;
                                background-color: transparent;
                                
                                .form-option-image{
                                    margin-left: auto;
                                    margin-right: auto;
                                    border-radius: 1rem;
                                }
                            }
                        }
                    }
                }
                .form-option-wrapper {
                    .form-option-expanded {
                        @include display-flex(flex);
                        @include flex-direction(column-reverse);

                        span.name {
                            @extend .fs-12;
                            font-weight: 600;
                            margin-top: 0.4rem;
                        }
                    }
                }
            }

            .form-label {
                @extend .fs-13;
                color: $color-primary;
                font-weight: 600;
                margin-bottom: 1rem;

                small {
                    @extend .fs-13;
                    color: $color-secondary;
                }
            }

            .form-option-wrapper {
                font-size: 0;

                @include tablet {
                    margin-bottom: 0.8rem;
                }

                // .form-option {
                //     padding: 0.4em;
                //     border: 0.1rem solid $color-greyLight;
                //     margin-right: 0.6rem;
                //     margin-bottom: 0;

                //     .form-option-variant {
                //         padding: 0;
                //         @extend .fs-13;
                //         line-height: 1.1;
                //         color: $color-textBase;
                //     }
                // }
            }
        }

        .form-radio {
            +.form-label {
                width: auto;
                padding: 1rem 1rem 1rem 3.5rem;
                border: 0.1rem solid $color-greyLight;
                @include border-radius(1.2rem);
                @extend .fs-13;
                line-height: 1.1;
                font-weight: 400;
                color: $color-textBase;
                margin: 0 1rem 0 0;

                @include tablet {
                    margin-bottom: 0.8rem;
                }

                &::before {
                    left: 1rem;
                    top: 50%;
                    transform: translate(0, -50%);
                    width: 1.7rem;
                    height: 1.7rem;
                    border-color: $color-greyLight;
                }

                &::after {
                    top: 50%;
                    transform: translate(0, -50%);
                    left: 1.1rem;
                    width: 1.5rem;
                    height: 1.5rem;
                    background-color: $color-secondary;
                }

                &:last-child {
                    display: inline-block;
                }
            }
        }

        .form-select {
            max-width: 100%;
        }

        .form-input {
            @include tablet {
                margin-bottom: 0.8rem;
            }
        }

        .select2 {
            @include tablet {
                margin-bottom: 0.8rem;
            }
        }

        .form-fileDescription {
            @extend .fs-13;

            @include tablet {
                margin-bottom: 0.8rem;
            }
        }

        // [data-product-attribute="swatch"] {
        //     .form-option-wrapper {
        //         .form-option {
        //             padding: 0.3rem;
        //             margin-bottom: 0.2rem;
        //         }

        //         .form-option-variant {
        //             width: 1.8rem;
        //             height: 1.8rem;
        //         }
        //     }
        // }

        [data-product-attribute="input-file"] {
            .form-file {
                margin-bottom: 1rem;
                @extend .fs-13;
                line-height: 1.5;
                color: $color-primary;
                display: block;

                ~label {
                    margin-left: 1.2rem;

                    input[type="checkbox"] {
                        margin-left: 0.6rem;
                        position: relative;
                        visibility: hidden;

                        &::before {
                            content: " ";
                            position: absolute;
                            height: 1.6rem;
                            width: 1.6rem;
                            border: 0.1rem solid $color-primary;
                            @include border-radius(1.2rem);
                            top: 0;
                            visibility: visible;
                        }

                        &::after {
                            content: " ";
                            position: absolute;
                            width: 1.6rem;
                            height: 1.6rem;
                            top: 0.8rem;
                            margin-top: 0.7rem;
                            left: 0;
                            background-image: url("data:image/svg+xml;charset=utf8,%3Csvg xmlns='http://www.w3.org/2000/svg' width='24' height='24' viewBox='0 0 24 24'%3E%3Cpath d='M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41z' fill='rgba(51, 65, 85, 1)' /%3E%3C/svg%3E");
                            background-repeat: no-repeat;
                            background-size: contain;
                        }

                        &:checked {
                            &::before {
                                border-color: $color-primary;
                            }

                            &::after {
                                visibility: visible;
                            }
                        }

                    }
                }
            }
        }

        .form-radio:checked+.form-label {
            &::before {
                border-color: $color-textBase;

            }
        }

        // .form-radio:checked+.form-option,
        // .form-radio:checked+.form-label {
        //     box-shadow: none;
        //     border-color: $color-textBase;
        // }

        .form-actions {
            margin-top: 2rem;
        }
    }
}

.cart {

    .cart-item-info,
    .cart-item-title {
        @include mobile {
            clear: both;
            width: calc(100% - 33.33%);
            margin-left: auto;
            padding: 0 1.5rem;
            height: auto;

        }

        .cart-item-label {
            @include tablet {
                text-align: left;
                font-weight: 600;
            }
        }
    }
}

.cart-totals {
    @include desktop-small {
        width: 60%;
    }

    @include mobile {
        width: 100%;
        margin-bottom: 0.5rem;
    }

    .cart-total+.cart-total {
        border-top: 0.1rem solid $color-greyLight;
    }

    .cart-total-label {

        @include desktop-small {
            text-align: left;
        }

        @include mobile {
            text-align: left;
            width: 50%;
        }
    }

    .cart-total-value {

        @include desktop-small {
            text-align: right;
            width: 50%;
        }
    }

    .button {
        @extend .fs-13;
    }

    .cart-gift-certificate-form,
    .coupon-form {
        .form-input {
            width: 75%;
            @include mobile{
                width: 100%;
            }

            +.button {
                margin-left: 2rem;
                @include mobile{
                    width: 100%;
                    margin-left: 0;
                    margin-top: 2rem;
                }
            }
        }
    }
}
.cart-content-padding-right{
    @include tablet-small{
        padding-right: 0;
    }
}

.cart-content-padding-right.cart-actions {
    .button {
        padding: 1.5rem 1.6rem 1.2rem;
        line-height: 1.5;
        max-width: 25rem;
        width: 100%;

        @include mobile {
            max-width: 100%;
        }
    }
}

.modal--alert {
    @extend .pt-40;
    @extend .pb-40;
    .alert-icon{
        margin-top: 0;
    }
    .modal-content {
        @extend .fs-13;
    }

    .button-container {
        .confirm.button {
            background: $color-primary;
            color: $color-white;
            border: 0.1rem solid $color-primary;

            &:focus {
                background: $color-primary;
                color: $color-white;
                border-color: transparent;

            }

            &:hover {
                color: $color-white;
                background: $color-secondary;
                border-color: $color-secondary;
            }
        }

        .cancel.button {
            background: $color-white;
            border: 0.1rem solid $color-primary;
            color: $color-primary;

            @include mobile-small {
                margin-left: 0.7rem;
            }

            &:hover,
            &:focus {
                color: $color-white;
                background: $color-primary;
                border: 0.1rem solid $color-primary;
            }
        }
    }
}
.previewCart-additionalCheckoutButtons {
  .or-use-label {
    display: none;
  }

  .CheckoutButton {
    width: 100%;

   div {
      float: none;
      display: block;
      width: 100%;
      max-width: 100%;
      padding: 0 1rem;
      border-radius: 100px;
          overflow: hidden;
    }
  }
}
.cart-additionalCheckoutButtons{
    margin-top: 1.2rem;
    .or-use-label{
        display: none;
    }
    .paypal-buttons{
        padding: 1.4rem 1.2rem 1rem;
        border-radius: 100px;
        overflow: hidden;
    }
}

dh-custom/compare.scss:
.template-compare {
    .page {
        margin: 0 -1rem;
    }
}


.compareTable-headingText {
    @extend .fs-14;
    font-weight: 700;
    text-transform: uppercase;
}

.compareTable-item {
    padding: 2rem 1.5rem;
    @extend .fs-13;

    @include tablet {
        padding-top: 4rem;
    }

    ul {
        margin-left: 0 !important;
        margin-bottom: 0;
        margin-left: 1.3rem;
        list-style: none;

        li {
            position: relative;
            margin-bottom: 0.4rem;
            @extend .fs-13;

            &::after {
                content: "";
                position: absolute;
                width: .3rem;
                height: .3rem;
                border-radius: 50%;
                @extend .bg-secondary;
                left: -1.3rem;
                top: 0.8rem
            }
        }
    }

    li {
        padding-left: 0 !important;

        &::before {
            display: none;
        }
    }
}

.compareTable-removeProduct {
    height: 2rem;
    width: 2rem;
    background-color: transparent;

    .icon {
        fill: $color-primary;
        margin-top: 0;
        height: 100%;
        width: 100%;
        vertical-align: unset;
        padding: 0;
    }

    &:hover {
        svg {
            fill: $color-secondary;
        }
    }
}

.compareTable-action {
    padding: 0 1.5rem 2rem;

    .button {
        padding: 1.1rem 1.4rem;
    }
}

.compareTable-heading {
    padding: 2rem 1.5rem;
}

.compareTable-row {
    th {
        width: 32rem;

        &:first-child {
            @include tablet {
                width: 0;
            }
        }
    }

}

.comparTableCustom {
    overflow-x: auto;
}

.compareTable-product {
    padding: 3rem 1.5rem 0;
}

.compareTable-product {
    .card-body {
        .card-text {
            a {
                color: inherit;

                &:hover {
                    color: $color-secondary;
                }
            }
        }

        .price-section {
            margin-top: auto;

            span {
                color: $color-secondary;
            }
        }
    }

    .card {
        border: 0;
    }
}

dh-custom/account.scss:
.top-banner-section.mb40 {
    margin-bottom: 4rem !important;
}

.form-row .form-field {
    padding-left: 1.5rem;
    padding-right: 1.5rem;
}

.account-body {
    .form-field {
        margin-bottom: 3rem;

        @include tablet-small {
            margin-bottom: 2rem;
        }

        .select2.select2-container,
        .select.form-select {
            width: 100% !important;

            // .select2-selection--single {
            //     padding: 0.8rem 0;
            // }
        }

        .select2-container--default {
            .select2-selection--single {
                .select2-selection__arrow {
                    right: 0.5rem;
                }
            }
        }
    }

    .form-actions {
        @include display-flex(flex);
        @include align-item(center);
        @include justify-content(center);
        margin-top: 0;


        .button {
            @include display-flex(flex);
            @include justify-content(center);
            @include align-item(center);
            line-height: 1.5rem;
        }

    }

}

ul.new-customer-fact-list {

    @extend .custom-ul;

    li {
        &::after {
            top: 0.8rem;
        }
    }
}

.form-field {
    .form-input[rows] {
        height: 14.1rem;
    }
}

.form-row {
    margin: 0 -1.5rem;
}

.form-label {
    font-family: 'Raleway', Arial, sans-serif;
}

.form {
    .g-recaptcha+br {
        display: none;
    }

    .form-actions {
        margin-top: 0;
        text-align: left;
        max-width: 100%;

        @include mobile {
            margin-top: 1rem;
            @include display-flex(flex);
            @include align-item(center);
            @include flex-wrap(wrap);
        }

        .button,
        input {
            @include mobile {
                width: 100%;
            }

            +.button {

                @include mobile {
                    margin-top: 1.6rem;
                }
            }
        }
    }
}

.template-newsletter_subscribe,
.template-createaccount_thanks {
    .page-content {
        min-height: 15vw;
        width: 100%;
        @include display-flex(flex);
        @include align-item(center);
        @include flex-direction(column);
        @include justify-content(center);
    }
}

/* ========== LOGIN PAGE ========== */

.login {
    margin: 0;
    max-width: 100%;

    @include mobile {
        margin-top: 3rem;
    }

    .alertBox {
        margin-top: 3rem;
    }

}

.login-row {
    @extend .mt-60;
    @include display-flex(flex);
    @include flex-wrap(wrap);
    @include align-item(center);
    // max-width: 80rem;
    margin-left: auto;
    margin-right: auto;
    margin-bottom: 6rem;

    &:after,
    &:before {
        display: none;
    }

    @include tablet {
        max-width: 100%;
        margin-top: 0;
    }


    .form-inlineMessage {
        margin: 0.5rem 0 0 0;
        order: 1;
        line-height: normal;
    }
}

.login-form {
    padding-left: 0;
    padding-right: 1.5rem;
    margin: 0;
    float: none;
    width: 50%;

    @include mobile {
        width: 100%;
        padding: 0;
        padding-bottom: 2rem;
    }

    .form-actions {

        @include mobile {
            @include display-flex(flex);
            @include flex-wrap(wrap);
            @include align-item(center);
        }

        .button {
            text-transform: capitalize;

            @include mobile {
                width: 100%;
            }
        }


        .forgot-password {
            display: inline-block;
            vertical-align: middle;
            margin-left: 2rem;
            font-weight: 500;
            text-decoration: underline;
            text-underline-offset: 0.5px;
            @extend .fs-14;

            @include mobile {
                width: 100%;
                margin-left: 0;
                display: block;
                text-align: center;
            }

        }

        .button {
            display: inline-block;
            vertical-align: middle;
            text-transform: capitalize;

        }
    }


}

.panel-header,
.panel-body {
    background: $color-thirdLighter;
    @include border-radius(1.2rem);
}

.new-customer {
    padding-left: 1.5rem;
    padding-right: 0;
    float: none;
    width: 50%;

    @include mobile {
        width: 100%;
        padding: 0;
    }

    .panel-header {
        padding: 3rem 3rem 0;
        background: $color-secondaryLight;
        @include border-radius(1.2rem);
        border-bottom-left-radius: 0;
        border-bottom-right-radius: 0;

        .panel-title {
            @extend .fs-24;
            font-weight: 600;
        }
    }

    .panel-body {
        padding: 2rem 3rem 3rem;
        margin-bottom: 0;
        background: $color-secondaryLight;
        @include border-radius(1.2rem);
        border-top-left-radius: 0;
        border-top-right-radius: 0;
    }
}

.new-customer-intro {
    margin-bottom: 1.5rem;
    @extend .fs-13;
}

.new-customer-fact-list {
    margin-left: 2rem;
    margin-bottom: 3rem;

    .new-customer-fact {
        padding-left: 0.5rem;
    }

    li {
        +li {
            margin-top: 0.5rem;
        }
    }
}

/* ========== CREATE ACCOUNT PAGE ========== */

.template-createaccount {
    .account--fixed {
        width: 100%;

        .account-body {
            margin-left: 0;
            margin-right: 0;

            .g-recaptcha {
                >div {
                    margin: 0 auto;
                    margin-left: 0;
                }
            }

            .form-actions {
                text-align: left;
                margin-top: 2.4rem;

                @include tablet-small {
                    margin-top: 0.8rem;
                }

                .button {
                    min-width: 30.3rem;
                    margin-left: 0;
                }
            }
        }

        @include tablet-small {
            max-width: 100%;
        }
    }
}

/* ========== RESET PASSWORD PAGE ========== */

.template-forgotpassword {
    .password-inner {
        max-width: 80%;
        margin: 0 auto;
    }

    .account {
        max-width: 80rem;
        width: 100%;

        @include tablet {
            max-width: 80%;
        }

        @include mobile {
            max-width: 100%;
        }
    }

    .page-heading {
        +p {
            text-align: center;
            max-width: 60%;
            margin-left: auto;
            margin-right: auto;

            @include tablet-small {
                max-width: 100%;
            }
        }
    }

    .form-prefixPostfix--centeredColumn.wrap {
        flex-direction: row;
        text-align: left;
        width: 100%;

        .g-recaptcha {
            width: auto;
            margin-top: 3rem;
            margin-left: 0;
            order: 4;

            @include mobile {
                margin-top: 1.7rem;
                order: 3;
            }
        }

        .form-input {
            width: 50rem;
            flex: inherit;
            order: 1;
            @include tablet-small{
                width: calc(100% - 21.3rem);
            }
            @include mobile {
                width: 100%;
            }
        }

        .form-inlineMessage {
            order: 3;
            width: 50rem;

            @include mobile {
                order: 2;
            }
        }
    }

    .forgot-password-form {
        .form-label {
            display: block;
            width: 100%;
            text-align: left;
        }

        .button {
            margin: 0 0 0 2rem;
            @include border-radius(10rem);
            order: 2;

            @include mobile {
                margin-top: 0.7rem;
                margin-left: 0;
                order: 4;
            }
            @include mobile {
             
                width: 100%;
            }
        }
    }
}

.forgot-password-form {
    margin-top: 5rem;

    @include mobile {
        margin-top: 0;
    }
}

/* ========== ACCOUNT NAVIGATION ========== */
.navBar--sub {
    display: block;
    @extend .pb-40;
    margin-top: 2.5rem;
    margin-bottom: 0;

    @include tablet-small {
        margin-bottom: 3rem;
        padding-bottom: 0rem;
    }

    @include mobile {
        margin-top: 3rem;
        margin-bottom: 1.5rem;
    }

    .navBar-item {
        margin: 0 1.5rem;

        @include tablet-small {
            display: inline-block;
            margin: 0 0.7rem;
        }

        @include mobile {
            margin: 0 1rem;
            margin-bottom: 1rem;
        }

        &::before {
            display: none;
        }
        &.navBar-item--separate{
            padding-left: 1.5rem;
            @include tablet-small{
                padding-left: 0.7rem;
            }
            @include mobile{
                padding-left: 1rem;
            }
        }
        .navBar-action {
            @extend .fs-16;
            line-height: 1.5;
            color: $color-textBase;
            position: relative;
            text-underline-offset: 0.2rem;
            font-weight: 600;

            @include mobile {
                font-size: 1.3rem;
            }

            &:hover {
                text-decoration: underline;
                color: $color-secondary;

            }
            .toggleLink-text{
                font-size: inherit;
                font-weight: inherit;
            }

        }

        .logout {
            color: $color-primary;
            font-weight: 700;


        }

        &.is-active {
            color: $color-secondary;
            border: 0;
            text-decoration: underline;

            .navBar-action {
                color: $color-secondary;
                text-decoration: none;
            }
        }

        .navBar-action-color--active {
            text-decoration: underline;
            font-weight: 600;
            color: $color-secondary;
        }
    }
}

/* ========== ACCOUNT PAGES COMMON ELEMENT========== */

.account {
    margin-bottom: 0;
    margin-top: 6rem;
    max-width: 69.444vw;
    margin-left: auto;
    margin-right: auto;

    @include tablet {
        max-width: 100%;
    }

    @include mobile {
        max-width: 100%;
        margin-top: 3rem;
        margin-bottom: 4rem;
    }

    // &.account--fixed,
    // &.account--fixedSmall,
    // &.account--fixedLarge {
    //     width: 100%;
    // }

    .form-inlineMessage {
        margin: 0.5rem 0 0 0;
        order: 1;
        line-height: normal;
        @extend .fs-13;

        @include mobile {
            order: 0;
        }
    }
}

.account-head {
    margin-bottom: 0rem;
}

.account-heading {
    font-size: 1.8rem;
    font-weight: 600;
    padding-bottom: 2rem;
    margin-bottom: 2rem;
    border-bottom: 0.01rem solid $color-greyLight;

    @include tablet-small {
        padding-bottom: 1.7rem;
    }
}

.account-list {
    margin: 0;
}

.account {
    h2:not(.page-heading) {
        @extend .account-heading;
        //margin-bottom: 3rem;
    }
}

/* ========== ORDER LISTING PAGES ========== */
.account{
    max-width: 100%;
    &--fixed,&--fixedLarge{
        width: 100%;
    }
}
.account-list {
    margin-bottom: 2rem;

    li:before {
        display: none;
    }

    .account-listShipping {
        padding: 0 0 2rem 0;

        .account-listShipping-title {
            @extend .fs-13;
            text-transform: uppercase;
            font-weight: 600;
        }
    }
}

.account-listItem {
    padding: 2rem 0;
    border-bottom: 0.01rem solid $color-greyLight;
    &:last-child{
        border-bottom: none;
        padding-bottom: 0;
    }
    @include tablet-small {
        padding: 1.7rem 0;
    }

    .account-product-figure {
        img {
            @include tablet-small {
                max-height: inherit;
            }
        }
    }
}

.account-product-price {
    @extend .fs-13;
}

.account-product-title {
    @extend .fs-14;
    margin-bottom: 0.5rem;
    text-transform: uppercase;



    a {
        text-transform: uppercase;
        font-weight: 600;
    }
}

.address-title {
    text-transform: capitalize;
    font-weight: 600;
}

.account-product-description {
    margin-bottom: 1.5rem;
    @extend .fs-13;
}

.account-product-details {
    margin: 0 -1.5rem;

    .account-product-detail {
        padding: 0 1.5rem;

        .account-product-detail-heading,
        span {
            @extend .fs-13;
            margin: 0;
            font-weight: 400;
            text-transform: capitalize;

        }
        .account-product-detail-heading{
            font-weight: 600;
            color: $color-primary;
        }
    }
}

.account-orderStatus {
    @extend .fs-13;

    @include mobile {
        position: relative;
        margin-bottom: 1rem;
    }
}

.account-orderStatus-label {
    padding: 0.8rem 1rem;
    @extend .fs-13;
    font-weight: 500;
    margin: 0;
}

.account-body {
    @include row;

    .form-actions {
        @include justify-content(flex-start);

        @include mobile {
            margin-top: 1rem;
        }

        .button {
            margin: 0 auto;
        }
    }
}

.account-content {
    @include col;
}

.account-sidebar {
    @include col;

    @include tablet-small {
        float: none;
        width: 100%;
        clear: both;
        margin-top: 2rem;
    }

    @include mobile-small {
        .account-reorder-form {
            width: 100%;
        }

        .button {
            width: 100%;
            @include justify-content(center);
        }
    }
}

.account-content--fixedSmall {
    width: 60%;

    @include tablet-small {
        width: 100%;
        float: none;
    }
}

.account-sidebar-block {
    @extend .fs-14;

    +.account-sidebar-block {
        @include mobile {
            margin-top: 2rem;
        }
    }
}

.account-product--alignMiddle .account-product-checkItem {
    width: 3rem;
}

.account-product--alignMiddle {

    .account-product-checkItem,
    .account-product-figure {
        left: auto;
    }

    .account-product-checkItem {
        .form-label {
            width: 2.2rem;
            height: 2.2rem;

            &::before {
                width: 2.2rem;
                height: 2.2rem;
                border-radius: 0;
                border: 0;
                background: transparent;
                margin-top: -0.07rem;
            }

            &::after {
                background-image: url("data:image/svg+xml,%3Csvg width='8' height='8' viewBox='0 0 8 8' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M1 4L3 6L7 2' stroke='white' stroke-width='1.5'/%3E%3C/svg%3E%0A");
                background-size: 1.4rem auto;
                background-color: $color-primary;
                margin-top: -0.8rem;
                left: 0;
                width: 2.2rem;
                height: 2.2rem;
            }
        }
    }

    .account-product-body {
        padding-left: 3rem;

        @include tablet-small {
            padding-left: 1.5rem;
        }
    }
}

.account-product-body {

    @include mobile {
        padding-bottom: 0;
    }

    .definitionList {
        @extend .fs-13;

        @include tablet-small {
            margin-top: 0;
        }

        +p {
            margin-top: 2rem;
        }
        &-key {
            @extend .fs-13;
            color: $color-primary;
            font-weight: 600;
        }
    }
}

.account-product-figure {
    width: 10.417vw;
    margin: 0 2rem 0 0;

    @include tablet-small {
        width: 10rem;
        vertical-align: middle;
    }

    img {
       width: 100%;
    }

    &:after {
        display: none;
    }
}

.account-order-address {
    li {
        margin-top: 0.5rem;
    }
}

.definitionList-value {
    margin-bottom: 0.4rem;
}

.account-orderTotal {
    margin: 0;

    @include tablet-small {
        float: none;
        width: 100%;
        margin-top: 0;
    }
}

.template-account_order {
    .account-list {
        @include mobile {
            margin: 0;
        }
    }

    .account-sidebar {
        .button {
            @include display-flex(flex);
            @include align-item(center);
            line-height: 1.5rem;
        }
    }
}

.account-sidebar-block .definitionList,
.account-order-address {
    @extend .fs-13;

    .definitionList-key {
        @extend .fs-13;
        color: $color-primary;
        font-weight: 600;
    }

    li {
        padding-left: 0;

        &::before {
            display: none;
        }
    }
}

/* ========== MESSAGE PAGE ========== */

.account-message {
    .account-product-title {
        color: $body-font-color;
        @extend .fs-13;
        margin-bottom: 1rem;
    }

    p {
        color: $color-primary;

        &:last-child {
            margin-bottom: 0;
        }
    }
}

.account-message {
    p.is-read {
        color: $color-primary;
        @extend .fs-13;
    }
}

/* ========== WISHLIST PAGE ========== */
.template-add-wishlist {
    .wishlist-add {
        .form-checkbox {
            +.form-label {
                line-height: 1.6rem;
            }
        }
    }
}

.wishlists-table {
    max-width: 100%;

    .table-tbody {
        .wish-name {
            color: $color-black;

            &:hover {
                color: $color-primary;
            }
        }
    }

    .table-actions {
        width: 10rem;

        .button {
            margin: 0;
            display: block;
            width: 100%;

            +.button {
                margin-left: 0;
                margin-top: 1rem;
            }
        }
    }

    +.form {
        margin-top: 2.6rem;

        @include tablet-small {
            margin-top: 1.7rem;
        }
    }
}

.share-wishlist {
    max-width: 60%;
    margin-bottom: 5rem;

    @include tablet-small {
        max-width: 100%;
    }

    .panel-body {
        padding: 3rem;

        h5 {
            font-size: 1.6rem;
            margin-bottom: 1.5rem;
        }

        @include mobile {
            padding: 2rem;
        }
    }

    .form-input[readonly] {
        background: $color-white;
    }
}

.template-wishlist {
    .body {
        .account {
            max-width: 100%;
        }
    }

    .productGrid {
        @include display-flex(flex);
        @include flex-wrap(wrap);

        .product {
            width: 25%;
            position: relative;
            @include display-flex(flex);
            @include flex-direction(column);
            margin-bottom: 3rem;

            @include tablet-small {
                width: 33.33%;
            }

            @include mobile {
                width: 50%;
            }

            .card {
                margin-bottom: 3rem;

                @include mobile {
                    margin-bottom: 2rem;
                }
            }

            .remove-item-button {
                position: static;
                margin-bottom: 0;

                .button {
                    width: 100%;
                }
            }

        }
    }
}


/* ========== Template Recent PAGE ========== */
.template-account_recentitems {
    .productGrid {
        .product {
            width: 25%;
            margin-bottom: 3rem;

            @include tablet {
                margin-bottom: 3rem;
                width: 33.33%;
            }

            @include mobile {
                width: 50%;
            }

            .brand-compare-section {
                .compare-btn {
                    display: none;
                }
            }
        }
    }
}

/* ========== ADDRESS PAGE ========== */

.template-account_addressbook {

    .account-body {
        margin-left: 0;
        margin-right: 0;

        .addressList {
            @include row;
            row-gap: 2rem;
            @include mobile {
                margin-left: 0;
                margin-right: 0;
                padding: 0;
            }
        }

        .address {
           @include col;
            margin-top: 0;
            @include mobile {
                padding: 0;
            }
        }

        .form-actions {
            .button {
                @include display-flex(flex);
                @include align-item(center);
            }
        }
    }
}

.panel--address {
    .panel-body {
        padding: 3rem;

        @include mobile {
            padding: 2rem;
        }
    }
}

.template-shippingaddressform {
    .body {
        overflow: visible;
    }

    .account-body {
        margin-left: 0;
        margin-right: 0;
    }
}

.addressList {
    @include row;
    @include display-flex(flex);
    @include flex-wrap(wrap);
    margin-bottom: 0;

}

.addressList {
    @include row;
    @include display-flex(flex);
    @include flex-wrap(wrap);
    margin-bottom: 0;

    @include tablet {
        margin: 0;
        padding: 0 1rem;
    }
}

.address {
    @include col;
    @include display-flex(flex);
    margin-top: 2rem;
    width: 33.3%;
    @include tablet-small{
        width: 50%;
    }
    @include mobile {
        width: 100%;
        padding-left: 0;
        padding-right: 0;
    }

    .panel--address {
        font-size: 1.6rem;
        height: 100%;
        width: 100%;
        -webkit-text-fill-color: initial;

        .panel-body {
            @include display-flex(flex);
            @include flex-direction(column);
            margin-bottom: 0;
            height: 100%;
        }
    }

    .address-details--postal {
        @include display-flex(flex);
        @include flex-direction(column);
        flex: 1 0 auto;

        &+.address-details {
