}

.gift-certificate-form {
    .form-column {
        padding-left: 1.5rem;
        padding-right: 1.5rem;

        >.form-field {
            padding-left: 0;
            padding-right: 0;
        }

        .form-field-group {
            border-radius: 0;
            border: 0.01rem solid $input-border-color;
            padding: 2rem;
            @include border-radius(1.2rem);
            margin-bottom: 3rem;

            @include tablet-small {
                margin-bottom: 1.7rem;

            }
        }
    }

    .form-checkbox+.form-label {
        @extend .fs-13;
        padding-left: 3rem;
        line-height: 1.6rem;
        font-weight: 600;

        &::before {
            width: 1.6rem;
            height: 1.6rem;
            border-radius: 0.3rem;
            border: 0;
            margin: 0;
            transform: translateY(-50%);
            margin-top: 0.01rem;
            top: 50%;
            background: transparent;
            border: 0.1rem solid $input-border-color;
        }

        &::after {
            width: 1.6rem;
            height: 1.6rem;
            border-radius: 0.3rem;
            margin: 0;
            transform: translateY(-50%);
            left: 0;
            top: 50%;
            background-image: url("data:image/svg+xml,%3Csvg width='9' height='7' viewBox='0 0 9 7' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M8.02548 0.469608L2.91407 5.67324L0.97416 3.69794C0.844456 3.56572 0.634208 3.56572 0.504504 3.6976C0.374801 3.82981 0.374801 4.04386 0.504504 4.17591L2.91423 6.6297L8.49513 0.947584C8.62484 0.815874 8.62484 0.601656 8.49513 0.469608C8.36543 0.337391 8.15518 0.337391 8.02548 0.469608Z' fill='%23A75A48' stroke='%23A75A48' stroke-width='0.35'/%3E%3C/svg%3E%0A");
            margin-top: 0.01rem;
            background-color: transparent;
        }
    }

    .form-radio+.form-label {
        @extend .fs-13;
        line-height: 1.8rem;
        padding-left: 3rem;
        font-weight: 600;

        &::before {
            width: 1.7rem;
            height: 1.7rem;
            @include border-radius(100%);
            background: rgba($color-secondary, 0.20);
            border: 0;
            top: 0.1rem;
        }

        &:after {
            width: 0.9rem;
            height: 0.9rem;
            background-color: $color-secondary;
            background-image: none;
            margin-top: 0;
            top: 0.5rem;
            left: 0.4rem;
            @include border-radius(100%);
            margin: 0;
            border: 0;
        }
    }

    .form-field--success {
        .form-radio+.form-label {
            &::before {
                border-color: $color-primary;
            }
        }
    }
    .form-actions{
        @include tablet-small{
            text-align: center;
        }
    }
    .select2.select2-container, .select.form-select{
        width: 100% !important;
        .select2-selection--single{
            min-height: 4.7rem;
        }
    }

}

.template-wishlists {
    .account--fixedLarge {
        .button--primary {
            @include mobile {
                margin-bottom: 0.5rem;
            }
        }

        .table--line {
            td {
                padding-top: 2rem;
                padding-bottom: 2rem;
            }

            th {
                @extend .fs-14;

                @include tablet-small {
                    font-size: 1.3rem;
                }
            }
        }

        .table-tbody {

            a {
                text-decoration: underline;
                @extend .fs-14;

                @include tablet-small {
                    font-size: 1.3rem;
                }
            }

            td {
                @extend .fs-14;

                @include tablet-small {
                    font-size: 1.3rem;
                }
            }

            .table-actions {
                .button {
                    padding: 1.4rem 1.3rem;
                    line-height: 1.5rem;
                    text-decoration: none
                }
            }

            .form-fieldset {
                .button+.button {
                    margin-top: 2rem;
                }
            }
        }

        .form-actions {
            input+.button {
                margin-left: 0.6rem;

                @include mobile {
                    margin-left: 0;
                }
            }
        }
    }

}

.table {
    thead {
        tr {
            th {
                @extend .fs-14;
            }
        }
    }
}

.wishlist-form {
    max-width: 100%;

    .form-field {

        @include mobile {
            padding: 0;
        }
    }
}



.alertBox {
    max-width: 80rem;
    margin: 0 auto 3rem;
    @extend .fs-13;

    @include tablet-small {
        width: auto;
    }
}


.body {
    .account {
        margin-top: 0;
    }
}

.navBar--account {
    .navBar-section {
        @include display-flex(flex);
        @include align-item(center);
        @include justify-content(center);
        @include flex-wrap(wrap);

        .wishlist-tab {
            display: none;
        }
    }
}

dh-custom/yotopo-review.scss:
.pages-product {
    .yotpo-review-card {
        padding: 0 1rem;
    }
    .yotpo-base-layout {
        padding-top: 0rem;

        .yotpo-head {
            height: auto;
        }

        .yotpo-title-text {
            font-size: 1.8rem;
            margin-top: 0;
            margin-bottom: 1.6rem;
        }

        .yotpo-body-text {
            margin-bottom: 2rem;
        }
        .yotpo-bottom-line{
            @include mobile {
                margin-top: 3rem;
            }
        }
        .yotpo-new-review-btn-wrapper {
            justify-content: center;

            @include mobile {
                margin-top: 0rem;
            }

            #yotpo-main-widget-btn {
                margin-bottom: 0;
                padding: 1.3rem 1.6rem;
                height: auto;
                @include border-radius(100px);
                outline: none;
                margin: 0;
                font-size: 1.4rem;
                font-weight: 600;
                transition: 0.4s ease-in-out;
                line-height: normal;
                background-color: $color-secondary;
                border-color: $color-secondary;

                &:hover {
                    background-color: $color-primary;
                    border-color: $color-primary;
                    color: $color-white;
                }
            }
        }
    }

    #yotpo-modal.yotpo-modal {
        @include border-radius(1.2rem);
    }

    /*========Yotop Share your thoughts==========*/
    .yotpo-review-form-modal,
    .yotpo-modal {
        &:not(.modal-backdrop){
            @include border-radius(1.2rem);
        }
        

        .btn-close {
            top: 1rem;
            right: -0.7rem;

            &:hover {
                color: $color-secondary;
            }
        }

        #modalTitle.yotpo-modal-header {
            margin: 0;
            font-size: 1.4rem;
            line-height: 1.5;
            padding: 2rem 0 2rem;
            border-bottom: 0.1rem solid #F4F5F6;

            .yotpo-modal-header {
                margin: 0;
            }
        }

        .modal-body {
            padding: 0 !important;

            .yotpo-star-rating-wrapper {
                margin-bottom: 1.6rem;
            }

            .yotpo-form-label,
            h4,
            .yotpo-file-input-headline,
            h6,
            h4 {
                font-family: 'Raleway', Arial, sans-serif;
                margin-bottom: 1.5rem;
                @extend .fs-13;
                line-height: 1.5;
                font-weight: 600;
                margin-top: 0;
                color: $color-primary;
                display: inline-block;
                position: relative;
                width: auto;
                max-width: fit-content;
                text-transform: capitalize;


                span {
                    font-size: 3rem;
                    color: $color-primary;
                    line-height: 1.2;
                    margin-bottom: 0;
                    height: auto;
                    display: inline-block;
                    position: absolute;
                    right: -1.5rem;
                }

                &.yotpo-star-rating-error {
                    color: #cb2222 !important;
                    text-transform: capitalize;
                    font-size: 1.2rem;
                    margin-top: 0.5rem;
                    display: block;
                    margin-bottom: 0;
                }
            }

            .yotpo-star-rating-wrapper {
                height: auto;
                min-height: auto !important;
            }

            .yotpo-star-rating-icons {
                margin-left: 0;
                min-height: min-content;

                .yotpo-star-rating-icons-wrapper {
                    padding: 0;

                    svg {
                        width: 1.7rem;
                        height: 1.7rem;
                        margin: 0 1rem 0 0;
                    }
                }

                .yotpo-star-rating-text {
                    margin: 0;
                    font-family: 'Raleway', Arial, sans-serif;
                    margin-bottom: 0rem;
                    @extend .fs-13;
                    line-height: 1.5;
                    font-weight: 600;
                }
            }

            .yotpo-input-wrapper {
                margin-bottom: 1.6rem;

                textarea,
                input {
                    @include border-radius(1.2rem);
                    color: $color-primary;
                    border: 0.1rem solid $input-border-color;
                    width: 100%;
                    @extend .fs-13;
                    padding: 1.15rem;
                }

                input::-webkit-input-placeholder,
                textarea::-webkit-input-placeholder {
                    color: $color-primary;
                    font-size: 1.2rem;
                }

                input::-moz-placeholder,
                textarea::-moz-placeholder {
                    color: $color-primary;
                    font-size: 1.2rem;
                }

                input:-ms-input-placeholder,
                textarea:-ms-input-placeholder {
                    color: $color-primary;
                    font-size: 1.2rem;
                }

                input:-moz-placeholder,
                textarea:-moz-placeholder {
                    color: $color-primary;
                    font-size: 1.2rem;
                }

            }

            .yotpo-new-input-validation {
                font-family: 'Raleway', Arial, sans-serif;
                font-size: 1.2rem;
                line-height: 1.5;
                font-weight: 600;
                color: #cb2222;
                text-transform: capitalize;
                margin-top: 0.5rem;
                margin-bottom: 0;
                display: block;
            }

            .yotpo-name-email-wrapper {
                width: 100%;
                min-width: inherit !important;
                flex-direction: unset !important;

                .yotpo-input-wrapper {
                    width: 50% !important;
                    padding: 0 1.5rem;
                    min-height: auto !important;
                    min-width: inherit !important;

                    &.yotpo-new-review-name,
                    &.yotpo-question-name {
                        padding-left: 0;
                    }

                    &.yotpo-new-review-email,
                    &.yotpo-question-email {
                        padding-right: 0;
                    }

                }
            }

            .yotpo-new-input-message {
                font-family: 'Raleway', Arial, sans-serif;
                margin-bottom: 0rem;
                @extend .fs-12;
                line-height: 1.5;
                color: $color-primary;
                font-weight: 600;
                text-transform: capitalize;
                margin-top: 0.6rem;
            }

            .yotpo-file-upload {
                .yotpo-upload-label {
                    margin-top: 0;
                    margin-bottom: 1.6rem;
                    border-color: $color-primary;
                    @include border-radius(1.2rem);
                    transition: 0.3s ease-in-out;
                    padding: 1.1rem 3rem !important;
                    height: auto;
                    width: 20rem;

                    &:hover {
                        background-color: $color-primary;
                        border-color: $color-primary;
                        color: $color-white;

                        svg {
                            path {
                                fill: $color-white;
                            }
                        }
                    }
                }

                .yotpo-uploaded-images {
                    padding: 0;
                }
            }

            .form-footer {
                padding: 0;
                position: inherit;
                display: block;

                h6 {
                    margin-bottom: 0;
                    font-family: 'Raleway', Arial, sans-serif;
                    @extend .fs-13;
                    line-height: 1.5;
                    font-weight: 600;
                    margin-top: 0;
                    display: block;
                    color: $color-primary;
                    margin-bottom: 2rem;
                    text-transform: capitalize;
                }

                .yotpo-new-review-submit,
                button {
                    height: auto;
                    @include border-radius(100px);
                    outline: none;
                    margin: 0;
                    padding: 1.6rem 4rem;
                    font-size: 1.4rem;
                    font-weight: 600;
                    transition: 0.4s ease-in-out;
                    line-height: normal;
                    background-color: $color-primary;
                    border-color: $color-primary;

                    &:hover {
                        background-color: $color-secondary;
                        border-color: $color-secondary;
                        color: $color-white;
                    }
                }
            }

            .yotpo-form-complete {
                height: auto;

                .yotpo-headline-complete {
                    font-family: 'Raleway', Arial, sans-serif;
                    margin-bottom: 1.6rem;
                    font-size: 2.4;
                    line-height: 1.5;
                    color: $color-primary;
                    font-weight: 600;
                }

                .yotpo-body-complete {
                    font-family: 'Raleway', Arial, sans-serif;
                    margin-bottom: 1.6rem;
                    font-size: 1.4rem;
                    line-height: 1.5;
                    color: $color-primary;
                    font-weight: 400;
                }

                .yotpo-shop-complete {
                    height: auto;
                    @include border-radius(100px);
                    outline: none;
                    margin: 0;
                    padding: 1.6rem 4rem;
                    font-size: 1.4rem;
                    font-weight: 600;
                    transition: 0.4s ease-in-out;
                    line-height: normal;
                    background-color: $color-primary;
                    border-color: $color-primary;

                    &:hover {
                        background-color: $color-secondary;
                        border-color: $color-secondary;
                        color: $color-white;
                    }
                }
            }
        }
    }

    .yotpo-modal.yotpo-form-complete {
        height: auto !important;
    }

    /*========Yotop height set==========*/
    .yotpo-device-laptop,
    .yotpo-display-l {
        .yotpo-review-form {

            .yotpo-new-review-content,
            .yotpo-new-review-headline {
                height: auto;
                min-height: auto !important;
            }
        }
    }

    .yotpo-display-m {
        .yotpo-review-form {
            .yotpo-new-review-headline {
                height: auto;
                width: 100% !important;
                min-height: auto !important;
            }
        }
    }


    /*========Yotop height set==========*/
    /*========Yotop Share your thoughts==========*/

    /*===================Q&A=======================*/
    .yotpo-device-laptop,
    .yotpo-device-desktop {
        .yotpo-question-form {
            .yotpo-question-content {
                min-height: inherit !important;
            }
        }
    }

    /*===================Q&A=======================*/
}

dh-custom/pdp-yotpo.scss:
.pages-product {
    .yotpo-base-layout {
        width: 100% !important;

        .ugc-storefront-widgets-tabs-container {
            padding-bottom: 1.5rem;
            border-bottom: 0.1rem solid #F5F5F6;
            margin-bottom: 3rem;
            margin-top: 0;
        }

        .ugc-storefront-widgets-tabs-container__tab {
            color: $color-primary;
            @extend .fs-16;
            font-weight: 600;
            line-height: normal;
            text-transform: uppercase;

