// =============================================================================
// COLORS (Tools)
// =============================================================================

@function colorUtilityClassName($color, $variant) {
    $colorFirstLetter: to-upper-case(str-slice($color, 0, 1));
    $variantFirstLetter: to-upper-case(str-slice($variant, 0, 1));
    $colorStr: $colorFirstLetter + str-slice($color, 2);
    $variantStr: "";

    @if $variant != "base" {
        $variantStr: $variantFirstLetter + str-slice($variant, 2);
    }

    $className: $colorStr + $variantStr;
    @return $className;
}

// =============================================================================
// COLORS (CSS)
// =============================================================================


// Bigcommerce Utility Classes
// -----------------------------------------------------------------------------
//
// 1. If set to false, there is no outputted CSS for this component.
//
// -----------------------------------------------------------------------------
// scss-lint:disable ImportantRule

@if $exportCSS--colorUtilities { // 1

    @each $color in map-keys($colorMap) {

        @each $variant in map-keys(map-get($colorMap, $color)) {

            .u-bg#{ colorUtilityClassName($color, $variant) } {
                background-color: color($color, $variant) !important;
            }

            .u-color#{ colorUtilityClassName($color, $variant) } {
                color: color($color, $variant) !important;
            }

        }

    }

}

// =============================================================================
// LAYOUT (Tools)
// =============================================================================

@mixin u-clearFloats($important: null) {

    &:before,
    &:after {

        // ---------------------------------------------------------------------
        //
        // 1. The space content is one way to avoid an Opera bug when the
        //    'contenteditable' attribute is included anywhere else in the
        //    document. Otherwise it causes space to appear at the top and
        //    bottom of the element.
        // 2. The use of `table` rather than `block` is only necessary if using
        //    ':before' to contain the top-margins of child elements.
        //
        // ---------------------------------------------------------------------

        content: " " $important; // 1
        display: table $important; // 2
    }

    &:after {

        clear: both $important;
    }
}

