$slick-dot-character: "\2022" !default;
$slick-dot-size: 6px !default;
$slick-opacity-default: 0.75 !default;
$slick-opacity-on-hover: 1 !default;
$slick-opacity-not-active: 0.25 !default;

@function slick-image-url($url) {
    @if function-exists(image-url) {
        @return image-url($url);
    }
    @else {
        @return url($slick-loader-path + $url);
    }
}

@function slick-font-url($url) {
    @if function-exists(font-url) {
        @return font-url($url);
    }
    @else {
        @return url($slick-font-path + $url);
    }
}

/* Slider */

.slick-list {
    .slick-loading & {
        background: #fff slick-image-url("ajax-loader.gif") center center no-repeat;
    }
}

/* Icons */
@if $slick-font-family == "slick" {
    @font-face {
        font-family: "slick";
        src: slick-font-url("slick.eot");
        src: slick-font-url("slick.eot?#iefix") format("embedded-opentype"), slick-font-url("slick.woff") format("woff"), slick-font-url("slick.ttf") format("truetype"), slick-font-url("slick.svg#slick") format("svg");
        font-weight: normal;
        font-style: normal;
    }
}

/* Arrows */

.slick-prev,
.slick-next {
    position: absolute;
    display: block;
    height: 20px;
    width: 20px;
    line-height: 0px;
    font-size: 0px;
    cursor: pointer;
    background: transparent;
    color: transparent;
    top: 50%;
    -webkit-transform: translate(0, -50%);
    -ms-transform: translate(0, -50%);
    transform: translate(0, -50%);
    padding: 0;
    border: none;
    outline: none;
    &:hover, &:focus {
        outline: none;
        background: transparent;
        color: transparent;
        &:before {
            opacity: $slick-opacity-on-hover;
        }
    }
    &.slick-disabled:before {
        opacity: $slick-opacity-not-active;
    }
    &:before {
        font-family: $slick-font-family;
        font-size: 20px;
        line-height: 1;
        color: $slick-arrow-color;
        opacity: $slick-opacity-default;
        -webkit-font-smoothing: antialiased;
        -moz-osx-font-smoothing: grayscale;
    }
}

.slick-prev {
    left: -25px;
    [dir="rtl"] & {
        left: auto;
        right: -25px;
    }
    &:before {
        content: $slick-prev-character;
        [dir="rtl"] & {
            content: $slick-next-character;
        }
    }
}

.slick-next {
    right: -25px;
    [dir="rtl"] & {
        left: -25px;
        right: auto;
    }
    &:before {
        content: $slick-next-character;
        [dir="rtl"] & {
            content: $slick-prev-character;
        }
    }
}

/* Dots */

.slick-dotted.slick-slider {
    margin-bottom: 30px;
}

.slick-dots {
    position: absolute;
    bottom: -25px;
    list-style: none;
    display: block;
    text-align: center;
    padding: 0;
    margin: 0;
    width: 100%;
    li {
        position: relative;
        display: inline-block;
        height: 20px;
        width: 20px;
        margin: 0 5px;
        padding: 0;
        cursor: pointer;
        button {
            border: 0;
            background: transparent;
            display: block;
            height: 20px;
            width: 20px;
            outline: none;
            line-height: 0px;
            font-size: 0px;
            color: transparent;
            padding: 5px;
            cursor: pointer;
            &:hover, &:focus {
                outline: none;
                &:before {
                    opacity: $slick-opacity-on-hover;
                }
            }
            &:before {
                position: absolute;
                top: 0;
                left: 0;
                content: $slick-dot-character;
                width: 20px;
                height: 20px;
                font-family: $slick-font-family;
                font-size: $slick-dot-size;
                line-height: 20px;
                text-align: center;
                color: $slick-dot-color;
                opacity: $slick-opacity-not-active;
                -webkit-font-smoothing: antialiased;
                -moz-osx-font-smoothing: grayscale;
            }
        }
        &.slick-active button:before {
            color: $slick-dot-color-active;
            opacity: $slick-opacity-default;
        }
    }
}

components/vendor/slick/slick.scss:
// SLICK
// -----------------------------------------------------------------------------
//
// Purpose: These are the styles for the base Slick carousel
//
// -----------------------------------------------------------------------------


//
// Next and Previous buttons for all carousels
//
// Purpose: Override the plugin to give some Stencil styling to the js generated mark-up
//
// 1. Set the background again because the plugin sets the full background property on hover
// 2. Prevent screen flicker when CSS transition is applied
//
// -----------------------------------------------------------------------------

.slick-next,
.slick-prev {
    @include carouselOpaqueBackgrounds($slick-arrow-bgColor);
    border: 1px solid $slick-arrow-borderColor;
    height: remCalc(61px);
    margin-top: -(remCalc(15px));
    padding: remCalc(10px);
    width: remCalc(40px);
    z-index: 1;

    &::before {
        background-position: 50%;
        background-repeat: no-repeat;
        background-size: 100%;
        display: block;
        height: remCalc(41px);
        width: remCalc(20px);
    }

    &:hover,
    &:focus {
        @include carouselOpaqueBackgrounds($slick-arrow-bgColor); // 1
        background-position: 50%;
        background-repeat: no-repeat;
        background-size: 100%;
    }
}

.slick-next {
    right: $slick-arrows-offset;

    @include breakpoint("large") {
        right: -(spacing("double") + spacing("quarter"));
    }

    &::before {
        @include carousel-arrow-color( $slick-arrow-color, "next");
    }

    &:hover {
        &::before {
            @include carousel-arrow-color( $slick-arrow-color-hover, "next");
        }
    }
}

.slick-prev {
    left: $slick-arrows-offset;

    @include breakpoint("large") {
        left: -(spacing("double") + spacing("quarter"));
    }

    &::before {
        @include carousel-arrow-color( $slick-arrow-color, "prev");
    }

    &:hover {
        &::before {
            @include carousel-arrow-color( $slick-arrow-color-hover, "prev");
        }
    }
}

.slick-slide img {
    display: block;
    width: 100%;
}


//
// Carousel dots
//
// Purpose: Stencil overrides for the js generated carousel dots
//
// 2. Nesting depth due to plugin css
// -----------------------------------------------------------------------------

.slick-dots {  // 2
    margin: 0;

    li {
        @include square(32px);
        margin: 0;
        padding: remCalc(5px);

        button,
        button::before {
            @include square(15px);
            border-radius: 50%;
        }

        button {
            border: 1px solid $slick-dot-color;
            display: inline-block;
            margin: 0;
            padding: 0;
            position: relative;
            text-align: center;
            vertical-align: middle;

            &:hover {
                &::before {
                    background: $slick-dot-color;
                }
            }
        }

        button::before {
            display: block;
            left: -1px;
            top: -1px;
            transition: background 100ms ease-out;
        }

        &.slick-active {
            button::before {
                background: $slick-dot-color-active;
            }
        }
    }

}

//
// Stencil override for product detail page thumbnail dots.
// -----------------------------------------------------------------------------
.productView .slick-dots {
    position: relative;
}


//
// disabled carousel elements
// -----------------------------------------------------------------------------
.slick-disabled {
    cursor: $cursor-default-value;
    opacity: 0.1;
}


//
// Slick track
// -----------------------------------------------------------------------------
.slick-track {
    backface-visibility: hidden; // 2
    perspective: 1000px; // 2
}

//
// iOS Safari fix https://github.com/kenwheeler/slick/issues/2834#issuecomment-389590661
// -----------------------------------------------------------------------------
div.slick-slider {
    min-width: 100%;
    width: 1px;
    *width: 100%;
}

//
// iOS Safari fix https://github.com/kenwheeler/slick/issues/2156
// -----------------------------------------------------------------------------
.slick-slide .card-image,
.slick-slide .heroCarousel-image {
    -webkit-touch-callout: default;
}

//
// Carousel tooltips for buttons and bullets
// -----------------------------------------------------------------------------

.carousel-tooltip {
    @include addFocusTooltip($attr: aria-label);
    display: none;
    height: 1px;
    margin-top: 10px;
    position: relative;

    @include breakpoint("small") {
        display: block;
    }

    &::after {
        padding: 15px 10px;
        top: 10px;
    }

    .slick-prev:focus &,
    .slick-next:focus &,
    .slick-dots button:focus & {
        &::before,
        &::after {
            display: block;
        }
    }

    .slick-prev &,
    .slick-next & {
        &::before {
            top: -7px;
        }
    }

    .slick-prev & {
        &::before {
            right: -2px;
        }

        &::after {
            left: -5px;
            right: auto;
        }
    }

    .slick-next & {
        &::after {
            right: -5px;
        }
    }

    .slick-dots button & {
        margin-top: 25px;

        &::before,
        &::after {
            right: 50%;
            transform: translateX(50%);
        }

        &::before {
            top: -7px;
        }
    }
}

//
// Carousel Play/Pause button
// -----------------------------------------------------------------------------

.carousel-play-pause-button {
    @include carouselOpaqueBackgrounds($slick-play-pause-button-bgColor);
    border: 1px solid $slick-play-pause-button-borderColor;
    bottom: spacing("third");
    color: $slick-play-pause-button-color;
    display: none;
    font-size: 14px;
    font-weight: 700;
    height: 32px;
    left: 15px;
    line-height: 1.25;
    max-width: 60px;
    min-width: 60px;
    overflow: hidden;
    position: absolute;
    text-overflow: ellipsis;
    transition: color 100ms ease-out;
    white-space: nowrap;
    z-index: zIndex("lowest");

    @include breakpoint("small") {
        font-size: 18px;
        max-width: 150px;
    }

    @include breakpoint("medium") {
        bottom: spacing("single");
        left: 25px;
    }

    &:hover {
        color: $slick-play-pause-button-color-hover;
    }

    @media (min-width: 375px) {
        max-width: 90px;
        min-width: 80px;
    }
}

components/foundation/global/global.scss:
// =============================================================================
// GLOBAL (CSS)
// =============================================================================
//
// 1. If set to false, there is no outputted CSS for this component.
// 2. Create offset for the fixed position header on smaller screens.
//
// -----------------------------------------------------------------------------

@if $exportCSS--global { // 1

    html,
    body {
        height: auto;
    }

    body {
        padding-top: $header-height; // 2
        @include breakpoint("medium") {
            padding-top: 0;
        }
    }

    figure {
        margin-bottom: $figure-marginBottom;
        margin-top: $figure-marginTop;

        > figcaption {
            margin-bottom: $figure-figcaption-marginBottom;
        }
    }

    figcaption {
        color: $figcaption-color;
        font-size: $figcaption-fontSize;
        margin-top: $figcaption-marginTop;
    }
}

components/foundation/type/type.scss:
// =============================================================================
// TYPE (CSS)
// =============================================================================

// Stencil type component additions
// -----------------------------------------------------------------------------


body {
    font-size: $body-fontSize;
    font-weight: $body-font-weight;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    font-weight: $header-font-weight;
    margin-top: $heading-marginTop;
}

h5,
h6 {
    text-transform: $heading-textTransform;
}

a {
    transition: $anchor-transition;
}

cite {
    line-height: $blockquote-cite-lineHeight;
    margin: $blockquote-cite-margin;
}

.page-heading {
    margin: 0 0 spacing("double");
    text-align: center;
}

h1 {
    font-size: $h1-font-size;
}

h2 {
    font-size: $h2-font-size;
}

h3 {
    font-size: $h3-font-size;
}

h4 {
    font-size: $h4-font-size;
}

h5 {
    font-size: $h5-font-size;
}

h6 {
    font-size: $h6-font-size;
}

.definitionList {
    @include clearfix;
}

.definitionList-key {
    color: stencilColor("color-textSecondary");
    float: left;
    font-weight: fontWeight("normal");
    margin: 0 spacing("quarter") 0 0;
}

.definitionList-value {
    margin: 0;
}

components/foundation/forms/forms.scss:
// =============================================================================
// FORMS (CSS)
// =============================================================================


// Select boxes
// -----------------------------------------------------------------------------

.form-select {
    appearance: none;
    background-image: $select-backgroundImage;
    background-position: $select-backgroundPosition;
    border-radius: $select-borderRadius;
    padding: $select-padding;

    &:hover {
        border-color: $select-hover-borderColor;
    }
}


// Form labels
// -----------------------------------------------------------------------------

.form-label {
    small {
        float: $opposite-direction;
        margin-top: $form-label-small-marginTop;
    }
}


// Form labels (inline small text)
// -----------------------------------------------------------------------------

.form-label--inlineSmall {
    small {
        color: stencilColor("color-textSecondary");
        float: none;
        font-size: inherit;
        font-weight: fontWeight("normal");
        margin-left: spacing("quarter");
        text-transform: none;
    }
}

components/foundation/tables/tables.scss:
// =============================================================================
// TABLES (CSS)
// =============================================================================


// Stencil table component additions
// -----------------------------------------------------------------------------

.table {
    margin-top: $table-marginTop;

    td + td {
        border-left: $table-td-borderLeft;
    }

    .table-alignCenter {
        text-align: center;
    }

    .table-alignRight {
        text-align: right;
    }
}

.table-actions {
    margin: spacing("single");
    text-align: center;

    .form {
        margin-bottom: 0;
    }

    .button {
        display: block;
        margin-left: 0;
        margin-right: 0;
        width: 100%;

        @include breakpoint("small") {
            display: inline-block;
            margin-bottom: 0;
            width: auto;
        }

        + .button {

            @include breakpoint("small") {
                margin-left: spacing("quarter");
            }
        }
    }
}


// Stencil table modifiers
// -----------------------------------------------------------------------------

.table--line {
    border-color: container("borderColor");
    border-width: 0 0 1px;

    .table-thead {
        background-color: stencilColor("body-bg");
        border-color: container("borderColor");
        border-width: 0 0 1px;

        tr {
            th {
                padding-left: 0;
                padding-right: 0;

                @include breakpoint("small") {
                    text-align: center;

                    &:first-child {
                        text-align: left;
                    }

                    &:last-child {
                        text-align: right;
                    }
                }

                + th {
                    padding-left: spacing("single");
                }
            }
        }
    }

    tr {
        border-color: container("border");

        td {
            display: block;
            padding-left: 0;
            padding-right: 0;

            @include breakpoint("small") {
                text-align: center;

                &:first-child {
                    text-align: left;
                }

                &:last-child {
                    text-align: right;
                }
            }

            + td {
                padding-left: spacing("single");
            }
        }
    }

    td + td {
        border-left: 0;
    }
}

components/foundation/modal/modal.scss:
// =============================================================================
// MODAL (CSS)
//
// // 1. Fix for content shifted to top in modal window when bottom variant option selected
// // 2. Icon styles and icon animations moved from the removed "sweetalert2" library
// =============================================================================

.modal {
    left: 50%;
    margin: 0;
    max-height: 90%;
    max-width: 95%;
    min-height: 240px;
    outline: none;
    overflow: hidden;
    padding: 0;
    top: 50% !important;
    transform: translate(-50%, -50%);

    .form-field {
        position: relative; // 1
    }
}

.modal--large {
    @include breakpoint("large") {
        width: $modal-width--large;
    }
}

.modal-header {
    padding-left: $reveal-modal-paddingHorizontal;
    padding-right: remCalc($reveal-close-height) + $reveal-close-side;
}

.modal-header-title {
    font-size: fontSize("large");
    text-align: center;

    @include breakpoint("medium") {
        font-size: fontSize("larger");
    }
}

.modal-close {
    @include square($reveal-close-height);

    // @include addFocusTooltip;
    color: stencilColor("icon-color");
    font-size: 26px;
    line-height: remCalc($reveal-close-height);
    padding: 0;
    position: absolute;
    text-align: center;
    text-decoration: none;
    z-index: zIndex("high");

    @include breakpoint("medium") {
        font-size: 32px;
    }

    &:hover {
        color: stencilColor("icon-color-hover");
    }

    &::before {
        right: 50%;
        transform: translateX(50%);
    }

    &::after {
        right: 50%;
        transform: translateX(remCalc(10px));
    }
}

.modal-body {
    overflow: auto;
    -webkit-overflow-scrolling: touch;
    padding: $reveal-modal-padding;
}

.modal--alert {
    background-color: $alert-modal-bgColor;
    border-radius: $alert-modal-borderRadius;
    font-family: $alert-font-body;
    padding: 40px 20px 20px;
    text-align: center;

    .alert-icon { // 2
        border: 0.25em solid transparent;
        border-radius: 50%;
        box-sizing: content-box;
        cursor: default;
        display: flex;
        font-family: inherit;
        height: 5em;
        justify-content: center;
        line-height: 5em;
        margin: 1.25em auto 1.875em;
        position: relative;
        user-select: none;
        width: 5em;
    }

    .error-icon {
        animation: animate-icon 0.5s;
        border-color: #f27474;
        color: #f27474;

        .icon-content {
            animation: animate-icon-content 0.5s;
            flex-grow: 1;
            position: relative;

            .line {
                background-color: #f27474;
                border-radius: 0.125em;
                display: block;
                height: 0.3125em;
                position: absolute;
                top: 2.3125em;
                width: 2.9375em;

                &-left {
                    left: 1.0625em;
                    transform: rotate(45deg);
                }

                &-right {
                    right: 1em;
                    transform: rotate(-45deg);
                }
            }
        }
    }

    .warning-icon { 
        animation: animate-icon 0.5s;
        border-color: #facea8;
        color: #f8bb86;

        .icon-content {
            align-items: center;
            animation: animate-icon-content 0.5s;
            display: flex;
            font-size: 3.75em;
        }
    }

    .modal-content {
        color: $alert-font-bodyColor;
        font-size: $alert-font-bodySize;
    }

    .button-container {
        margin: $alert-button-wrapperMargin;

        .confirm {
            background-color: $alert-button-bgColor;
            border-color: $alert-button-borderColor;
            color: $alert-button-color;
        }

        .confirm:focus,
        .confirm:hover {
            background-color: $alert-button-bgColorHover;
            border-color: $alert-button-borderColorHover;
            color: $alert-button-colorHover;
        }

        .confirm:active {
            background-color: $alert-button-bgColorActive;
            border-color: $alert-button-borderColorActive;
            color: $alert-button-colorActive;
        }
    }
}

.hide-content {
    opacity: 0;
}

//Icon animations
@keyframes animate-icon { // 2
    0% {
        opacity: 0;
        transform: rotateX(100deg);
    }

    100% {
        opacity: 1;
        transform: rotateX(0deg);
    }
}

@keyframes animate-icon-content {
    0% {
        margin-top: 1.625em;
        opacity: 0;
        transform: scale(0.4);
    }

    50% {
        margin-top: 1.625em;
        opacity: 0;
        transform: scale(0.4);
    }

    80% {
        margin-top: -0.375em;
        transform: scale(1.15);
    }

    100% {
        margin-top: 0;
        opacity: 1;
        transform: scale(1);
    }
}

// =============================================================================
// DROPDOWN (CSS)
// =============================================================================


.dropdown {
    left: -9999px;
    outline: none;
    position: absolute;
}

.dropdown-menu {
    z-index: $zIndex-lowest;

    &.is-loading .loadingOverlay {
        background: none;
        padding: (spacing("double") + spacing("single")) 0;
        position: relative;
    }

    &.is-open {
        display: block;
    }

    .button {
        &:focus {
            z-index: $zIndex-lowest;
        }
    }
}

.dropdown-menu-item {
    text-align: left;

    &:first-child {
        > a {
            padding-top: spacing("half");
        }
    }

    &:last-child {
        > a {
            padding-bottom: spacing("base");
        }
    }

    > a {
        margin: 0;
        padding: spacing("sixth") (spacing("half") + spacing("quarter"));
        text-decoration: none;
    }
}

.form-wishlist {
    position: relative;

    @include breakpoint("small") {
        float: left;
        padding: 0 remCalc(10);
        width: 50%;
    }

    @include breakpoint("medium") {
        padding: 0;
        width: auto;
    }

    @include breakpoint("large") {
        display: inline-block;
        margin-top: 1rem;
    }

    .button {
        width: 100%;

        @include breakpoint("medium") {
            width: auto;
        }

        @include breakpoint("large") {
            margin-right: spacing("half");
        }
    }
}

.dropdown-menu-button {
    width: 100%;

    &.is-open {
        border-bottom-left-radius: 0;
        border-bottom-right-radius: 0;
    }

    .icon {
        float: right;
        margin-left: 10px;
    }

    &:focus {
        z-index: $zIndex-lower;
    }
}

#wishlist-dropdown.is-open {
    background: transparent;
    left: auto !important;
    max-width: none !important;
    width: 100% !important;

    @include breakpoint("small") {
        padding-right: 20px;
    }

    @include breakpoint("medium") {
        padding-right: 0;
    }

    @include breakpoint("large") {
        padding-right: 11px;
    }

    &:focus {
        outline: none;
    }

    [data-wishlist] {
        border-top: 0;
        margin-bottom: 0;
        width: 100%;
    }

    .button {
        background-color: stencilColor("dropdown--wishList-backgroundColor");
        border-top-left-radius: 0;
        border-top-right-radius: 0;
        width: 100%;
    }

    .button--has-items {
        font-weight: 700;
    }

    li:not(:last-child) .button {
        border-bottom: 0;
        border-radius: 0;
        border-top: 0;
        margin-bottom: 0;
    }
}

components/foundation/breadcrumbs/breadc:
// =============================================================================
// BREADCRUMBS (CSS)
// =============================================================================
//
//
// 1. Hide breadcrumbs on mobile-sized screens to improve screen real estate.
//
// 2. Since our breadcrumbs are centred with a variable width, we need to use
//    display: inline-block; on the breadcrumbs. Hence needing to remove the
//    white-space with font-size: 0;
//    The font-size is reset in the breadcrumb settings file.
//
// -----------------------------------------------------------------------------

ol.breadcrumbs {
    display: none; // 1
    font-size: 0; // 2
    overflow: visible;
    text-align: center;
    @include breakpoint("small") {
        display: block; // 1
    }
}

.breadcrumb {
    display: inline-block;
    float: none;
    margin-right: 0.25rem;

    &.is-active {
        > .breadcrumb-label {
            cursor: pointer;
            font-weight: 700;
        }
    }

    // needs to override common bigcommerce styles for accessibility improvements
    &:not(:first-child)::before {
        border-right: 0.1em solid $primary-color;
        content: "";
        display: inline-block;
        height: 0.8em;
        margin: 0 0.25rem;
        transform: rotate(15deg);
    }
}

components/foundation/pagination/paginat:
// =============================================================================
// PAGINATION (CSS)
// =============================================================================


// Pagination list items
// -----------------------------------------------------------------------------
//
// 1. Centred list via inline-block. Font-size: 0; to remove white-space.
// 2. Specificity overrides for Foundation's inadequate selectors.
//
// -----------------------------------------------------------------------------

.pagination-list {
    @include clearfix;

    font-size: 0; // 1
    margin-bottom: spacing("single");
    text-align: center;

    .pagination-item { // 2
        display: inline-block;
    }
}


// Pagination list items
// -----------------------------------------------------------------------------
//
// 1. Specificity overrides for Foundation's inadequate selectors.
//
// -----------------------------------------------------------------------------

.pagination-item {
    border: $pagination-item-border;
    line-height: 1;

    .pagination-link {
        text-decoration: none;
        transition: $pagination-link-transition;

        &:hover {
            color: $pagination-link-color-hover;
        }
    }

    &.pagination-item--current { // 1
        border-color: $pagination-link-current-borderColor;
        border-radius: $pagination-link-current-borderRadius;
        margin: $pagination-link-current-margin;
    }

    &.pagination-item--previous, // 1
    &.pagination-item--next { // 1

        > a {
            color: $pagination-item-previousNext-color;

            &:hover {
                color: $pagination-item-previousNext-color-hover;

                .icon > svg {
                    fill: $pagination-item-previousNext-color-hover;
                }
            }
        }
    }
}

.pagination-item--previous,
.pagination-item--next {
    .icon {
        height: remCalc(20px);
        margin-top: -(remCalc(2px));
        width: remCalc(20px);
    }
}

.pagination-item--previous {
    float: left;

    .icon {
        margin-left: -(remCalc(12px));
    }
}

.pagination-item--next {
    float: right;

    .icon {
        margin-right: -(remCalc(12px));
    }
}


// Simple/small pagination (Next/Previous arrows are the only way to navigate)
// -----------------------------------------------------------------------------

.pagination-list--small {
    position: relative;

    .pagination-item {
        color: $pagination-list--small-color;
        cursor: inherit;
        line-height: $pagination-list--small-lineHeight;
    }

    .pagination-link {
        padding: $pagination-list--small-link-padding;
    }

    .pagination-item--previous,
    .pagination-item--next {
        position: absolute;
        top: 0;
    }

    .pagination-item--previous {
        left: 0;
    }

    .pagination-item--next {
        right: 0;
    }
}

components/foundation/accordion/accordio:
// =============================================================================
// ACCORDION (CSS)
// =============================================================================


.accordion {
    border-radius: $accordion-borderRadius;
    box-shadow: none;

    .navList {
        margin-bottom: 0;
    }
}

.accordion-content {
    border: 0;

    .form:only-child {
        margin-bottom: 0;
    }
}

.accordion-title {
    color: stencilColor("color-textHeading");
    font-family: "Montserrat", Arial, Helvetica, sans-serif;
    font-size: remCalc(15px);
    margin: 0;
}

.accordion-navigation {
    align-items: center;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    padding-right: 1rem;
    position: relative;
    text-decoration: none;
    width: 100%;
}


// Accordion nav list
// -----------------------------------------------------------------------------
//
// Purpose: Used as navigation menu, for example: faceted search menu.
//
// -----------------------------------------------------------------------------
.accordion--navList {
    @include breakpoint("medium") {
        border: 0;
    }

    .sidebarBlock + & {
        @include breakpoint("medium") {
            margin-top: $accordion-navList-spacing;
        }
    }

    .accordion-block {
        @include breakpoint("medium") {
            margin-top: $accordion-navList-spacing;
        }
    }

    .accordion-content {
        padding: $accordion-navList-padding;

        @include breakpoint("medium") {
            padding: 0;
        }
    }

    .accordion-navigation {
        @include breakpoint("medium") {
            background-position: right 0 top 50%;
            border: 0;
            margin-bottom: spacing("half") !important;
            padding: 0;
        }
    }

    .accordion-title {
        font-weight: normal;
        padding: $accordion-navList-padding;
        text-decoration: none;
        text-transform: inherit;

        @include breakpoint("medium") {
            padding: 0;
        }
    }

    .accordion-indicator {
        fill: color("greys", "light");
    }
}


// Accordion block
// -----------------------------------------------------------------------------
//
// Purpose: Child blocks of an accordion
//
// -----------------------------------------------------------------------------
.accordion-block:first-child {
    margin-top: 0;

    .accordion-navigation {
        border-radius: $accordion-borderRadius $accordion-borderRadius 0 0;
    }
}

.accordion-block:last-child {
    .accordion-navigation,
    .accordion-content {
        border-radius: 0 0 $accordion-borderRadius $accordion-borderRadius;
    }

    .accordion-navigation {
        border-bottom-width: 0;

        &.is-open {
            border-bottom-width: 1px;
            border-radius: 0;
        }
    }
}

.accordion-nav-clear-holder {
    position: relative;
}

.facetedSearch-clearLink {
    color: stencilColor("color-textSecondary");
    position: absolute;
    right: 40px;
    top: 50%;
    transform: translateY(-50%);
    @include breakpoint("medium") {
        right: 20px;
        top: 3px;
        transform: none;
    }

    &:hover {
        color: stencilColor("color-textSecondary--hover");
    }
}

components/foundation/buttons/buttons.sc:
// =============================================================================
// BUTTONS (CSS)
// =============================================================================

.button {
    line-height: 1rem;
    transition: all 0.15s ease;

    + .button {
        margin-left: 0;

        @include breakpoint("medium") {
            margin-left: spacing("half");
        }
    }
}

.button[disabled] {
    cursor: not-allowed;
}

.button--icon {
    svg {
        fill: stencilColor("button--icon-svg-color");
    }
}

components/foundation/inlineList/inlineL:
// =============================================================================
// INLINE LIST (CSS)
// =============================================================================
// 1. It prevents from missing top outline on refine filters on focus;
//
// -----------------------------------------------------------------------------

ul.inlineList { 
    @include clearfix; // 1
    overflow: visible;
}

.inlineList--labels {
    margin-bottom: 0;
    margin-left: -1 * spacing("half");

    > li {
        margin-bottom: spacing("half");
        margin-left: spacing("half");
    }
}

@mixin lazy-loaded-img() {
    bottom: 0;
    left: 0;
    margin: auto;
    position: absolute;
    right: 0;
    top: 0;
}

@mixin lazy-loaded-padding($size-param) {
    &::after {
        content: "";
        display: block;
        height: 0;
        padding-bottom: get-padding(stencilString($size-param));
        width: 100%;
    }
}

@mixin cart-item-lazy-load-img-placeholder() {
    &::after {
        content: "";
        display: block;
        height: 100%;
        left: 0;
        position: absolute;
        top: 0;
        width: 100%;
    }
}

components/foundation/alerts/alerts.scss:
