<script>
  const personalizeButton = document.querySelector("#personalize-button");
  personalizeButton.addEventListener("click", () => {
    personalizeButton.disabled = true;
  });

  const redirectToScratchProductButton = document.querySelector(
    "#redirect-to-scratch-product"
  );
  if (redirectToScratchProductButton) {
    redirectToScratchProductButton.addEventListener("click", () => {
      redirectToScratchProductButton.disabled = true;
    });
  }
</script>

<style>
  @keyframes loading {
    0% {
      transform: rotate(0deg);
    }

    100% {
      transform: rotate(360deg);
    }
  }
  .button.loading {
    color: transparent;
    z-index: 99999;
    cursor: not-allowed;
    opacity: 0.5;
  }
  .button.loading svg {
    display: none;
  }

  .button.loading::before {
    content: "";
    display: block;
    animation: loading 0.5s infinite linear;
    border: 0.2rem solid transparent;
    border-inline-start: 0.2rem solid white;
    border-radius: 100%;
    height: 2.4rem;
    width: 2.4rem;
    z-index: 999999;
    position: absolute;
    left: 50%;
    top: 50%;
    margin-top: -1.2rem;
    margin-left: -1.2rem;
    right: auto;
    bottom: auto;
    background: none;
    transform: none;
    transform-origin: unset;
  }
</style>
