<style>
  @keyframes loading {
    0% {
      transform: rotate(0deg);
    }

    100% {
      transform: rotate(360deg);
    }
  }
  .button.loading {
    color: transparent;
    z-index: 99999;
    cursor: not-allowed;
    opacity: 0.5;
  }
  .button.loading svg {
    display: none;
  }

  .button.loading::before {
    content: "";
    display: block;
    animation: loading 0.5s infinite linear;
    border: 0.2rem solid transparent;
    border-inline-start: 0.2rem solid white;
    border-radius: 100%;
    height: 2.4rem;
    width: 2.4rem;
    z-index: 999999;
    position: absolute;
    left: 50%;
    top: 50%;
    margin-top: -1.2rem;
    margin-left: -1.2rem;
    right: auto;
    bottom: auto;
    background: none;
    transform: none;
    transform-origin: unset;
  }
</style>

components/dh-custom/products/product-vi:
{{> components/customers-canvas/customers-canvas-uif}}

{{inject 'outOfStockDefaultMessage' (lang 'products.out_of_stock_default_message')}}

<div class="productView"
    data-event-type="product"
    data-entity-id="{{product.id}}"
    data-name="{{product.title}}"
    data-product-category="
    {{#each product.category}}
        {{#if @last}}{{this}}{{else}}{{this}}, {{/if}}
    {{/each}}"
    data-product-brand="{{product.brand.name}}"
    data-product-price="
    {{#or customer (unless settings.hide_price_from_guests)}}
        {{#if product.price.with_tax}}
            {{product.price.with_tax.value}}
        {{else}}
            {{product.price.without_tax.value}}
