                            "slidesToShow": 1,
                            "slidesToScroll": 1,
                            "arrows": true,
                            "asNavFor": ".slider-nav",
                            "slide": ".main-slide-item",
                            "rows": 0,
                            "dots": false,
                            "infinite": true,
                            "focusOnSelect": true,
                            "responsive": [
                                    {
                                        "breakpoint": 767,
                                        "settings": {
                                            "dots": true,
                                            "autoplay": true,
                                            "autoplaySpeed": 4000,
                                            "swipeToSlide": true
                                        }
                                    }
                            ]
                        }'>
                    {{#each product.images}}
