    <div class="description-left">
            
        {{> components/dh-custom/products/got-your-back}}
        {{> components/dh-custom/products/product-features}}
        
    </div>
    

    <article class="productView-description">
        {{!--{{#if theme_settings.show_product_details_tabs}}
